var book = {
	"name": "1 Makoronike",
	"numChapters": "29",
	"chapters":
	{
		"1":
		{
			"1": "<sup>1</sup> Adhamu, naSeti, naEnoshi;",
			"2": "<sup>2</sup> naKenani, naMaharareri, naJaredhi;",
			"3": "<sup>3</sup> naEnoki, naMetusera, naRameki;",
			"4": "<sup>4</sup> naNowa, naShemi, naHamu, naJafeti.",
			"5": "<sup>5</sup> Vanakomana vaJafeti: Gomeri, naMagogi, naMadhai, naJavhani, naTubhari, naMesheki, naTirasi.",
			"6": "<sup>6</sup> Vanakomana vaGomeri: Ashikenazi, naDhifati, naTogarima.",
			"7": "<sup>7</sup> Vanakomana vaJavhani: Erisha, naTarishishi, naKitimi, naRodhanimi.",
			"8": "<sup>8</sup> Vanakomana vaHamu: Kushi, naMiziraimu, naPuti, naKenani.",
			"9": "<sup>9</sup> Vanakomana vaKushi: Shebha, naHavhira, naSabata, naRaama, naSabhiteka. Vanakomana vaRaama: Shebha, naDhedhani.",
			"10": "<sup>10</sup> Kushi akabereka Nimurodhi; iye akatanga kuva munhu ane simba panyika.",
			"11": "<sup>11</sup> Mizirami akabereka Rudhimi, naAnamimi, naRehabhimi, naNafutuhimi,",
			"12": "<sup>12</sup> naPatirusimi, naKasiruhimi (VaFirisitia vakabva kwaari), naKafitorimi.",
			"13": "<sup>13</sup> Kenani akabereka Zidhoni mwana wake wedangwe, naHeti,",
			"14": "<sup>14</sup> navaJebhusi, navaAmori, navaGirigashi;",
			"15": "<sup>15</sup> navaHivhi, navaAriki, navaSini,",
			"16": "<sup>16</sup> navaArivhadhi, navaZemari, navaHamati.",
			"17": "<sup>17</sup> Vanakomana vaShemi: Erami, naAshuri, naAripakishadhi, naRudhi, naArami, naUzi, -naHuri, naGeteri, naMesheki.",
			"18": "<sup>18</sup> Aripakishadhi akabereka Shera, Shera akabereka Ebheri.",
			"19": "<sup>19</sup> Ebheri akaberekerwa vanakomana vaviri, zita romumwe raiva Peregi, nokuti pamazuva ake nyika yakakamurwa, zita romunun'una wake raiva Jokitani.",
			"20": "<sup>20</sup> Jokitani akabereka Arimodhadhi, naSherefi, naHazarimavheti, naJera,",
			"21": "<sup>21</sup> naHadhorami, naUzari, naDhikira,",
			"22": "<sup>22</sup> naEbhari, naAbhimaeri, .naShebha,",
			"23": "<sup>23</sup> naOfiri, naHavhira, naJobhabhu. Ava vose vaiva vanakomana vaJokitani.",
			"24": "<sup>24</sup> Shemi, naAripakishadhi naShera,",
			"25": "<sup>25</sup> naEbheri, naPeregi, naReu,",
			"26": "<sup>26</sup> naSerugi, naNahori, naTera,",
			"27": "<sup>27</sup> Abhuramu (ndiye Abhurahamu).",
			"28": "<sup>28</sup> Vanakomana vaAbhurahamu: Isaka, nalshimaeri.",
			"29": "<sup>29</sup> Ndiwo marudzi avo: Mwana wedangwe waIshimaeri, Neabhaioti, tevere Kedhari, naAbhidheeri, naMibhisami,",
			"30": "<sup>30</sup> naMishima, naDhuma, naMasa; naHadhadhi, naTema,",
			"31": "<sup>31</sup> naJeturi, naNafishi, naKedhema. Ndivo vanakomana vaIshimaeri.",
			"32": "<sup>32</sup> Vanakomana vaKetura, murongo waAbhurahamu: Iye wakabereka Zimirani, naJokishani, naMedhani, naMidhiani, naIshibhaki, naShua. Vanakomana vaJokishani: Shebha, naDhedhani.",
			"33": "<sup>33</sup> Vanakomana vaMidhiani: Efa, naEferi, naHanoki, naAbhidha, naEridha. Ava vose vaiva vanakomana vaKetura.",
			"34": "<sup>34</sup> Abhurahamu akabereka Isaka. Vanakomana vaIsaka: Esau, naIsiraeri.",
			"35": "<sup>35</sup> Vanakomana vaEsau: Erifazi, naReueri, naJeushi, naJarami, naKora.",
			"36": "<sup>36</sup> Vanakomana vaErifazi: Temani, naOmari, naZefi, naGatami, naKenazi, naTimina, naAmareki.",
			"37": "<sup>37</sup> Vanakomana vaReueri: Nahati, naZera, naShama, naMiza.",
			"38": "<sup>38</sup> Vanakomana vaSheiri: Rotani, naShobhari, naZibheoni, naAna, naDhishani.",
			"39": "<sup>39</sup> Vanakomana vaRotani: Hori, naHomami; Timina waiva hanzvadzi yaRotani.",
			"40": "<sup>40</sup> Vanakomana vaShobhari: Ariani naManahati, naEbhari, naShefi naOnami. Vanakomana vaZibheoni: Aia naAna.",
			"41": "<sup>41</sup> Vanakomana vaAna: Dishoni. Vanakomana vaDhishoni: Hamurani naEshibhani naItirani naKerani.",
			"42": "<sup>42</sup> Vanakomana vaEzeri: Bhirihani naZaavani, naJaakani. Vanakomana vaDhishani: Uzi naArani.",
			"43": "<sup>43</sup> Zvino ndiwo madzimambo akabata ushe panyika yaEdhomu, valsiraeri vachigere kuva namambo, Bhera mwanakomana waBheori; zita reguta rake raiva Dhinihabha.",
			"44": "<sup>44</sup> Bhera akafa, Jobhabhu mwanakomana waZera weBhozira akamutevera paushe.",
			"45": "<sup>45</sup> Jobhabhu akafa, Hushani wenyika yavaTemani akamutevera paushe.",
			"46": "<sup>46</sup> Hushani akafa, Hadhadhi mwanakomana waBhedhadhi, wakadeya kuuraya Midhiani pamunda waMoabhi, akamutevera paushe; zita reguta rake raiva Avhiti",
			"47": "<sup>47</sup> Hadhadhi akafa, Samira weMasireka akamutevera paushe.",
			"48": "<sup>48</sup> Shamira akafa, Shauri weRehobhoti paRwizi akamutevera paushe.",
			"49": "<sup>49</sup> Shauri akafa, Bhaarihanani mwanakomana waAkibhori akamutevera paushe.",
			"50": "<sup>50</sup> Bhaarihanani akafa, Hadhadhi akamutevera paushe; zita reguta rake raiva Pai; uye zita romukadzi wake raiva Mehetabheri, mukunda waMatiredhi, mukunda waMezahabhi.",
			"51": "<sup>51</sup> Hadhadhi akafa. Madzishe eEdhomu ndiwo: Ishe Timina, nashe Aria, nashe Jeteti,",
			"52": "<sup>52</sup> nashe Ohoribhama, nashe Era, nashe Pinoni,",
			"53": "<sup>53</sup> nashe Kenazi, nashe Temani, nashe Mibhazari,",
			"54": "<sup>54</sup> nashe Magidhieri, nashe Irami. Ndiwo madzishe aEdhomu."
		},
		"2":
		{
			"1": "<sup>1</sup> Ndivo vanakomana vaIsiraeri: Rubheni, naSimiyoni, naRevhi, naJudha, naIsakari naZebhuruni,",
			"2": "<sup>2</sup> naDhani, naJosefa naBhenjamini, naNafutari, naGadhi, naAsheri.",
			"3": "<sup>3</sup> Vanakomana vaJudha: Eri, naOnani, naShera; vanakomana ava vatatu akavaberekerwa naBhatishua muKenani. Asi Eri, mwana wedangwe waJudha, akanga ari munhu akaipa pamberi paJehovha, akamuuraya.",
			"4": "<sup>4</sup> Tamari mukadzi womwana wake, akamuberekera Perezi naZera. Vanakomana vose vaJudha vakanga vari vashanu.",
			"5": "<sup>5</sup> Vanakomana vaPerezi: Hezironi naHamuri.",
			"6": "<sup>6</sup> Vanakomana vaZera: Zimiri, naEtani, naHemani, naKarikori, naDhara; vose vari vashanu.",
			"7": "<sup>7</sup> Vanakomana vaKarimi: Akari, mutambudzi walsiraeri, wakatadza pachinhu chakanga chakayeriswa.",
			"8": "<sup>8</sup> Vanakomana vaEtani: Azara.",
			"9": "<sup>9</sup> Vanakomanawo vaHezironi, vaakaberekerwa: Jerameeri, naRami, naKerubhai.",
			"10": "<sup>10</sup> Rami akabereka Aminadhabhu, Aminadhabhu akabereka",
			"11": "<sup>11</sup> Nashoni, muchinda wavana vaJudha; \"Nashoni akabereka Sarima, Sarima akabereka Bhowasi;",
			"12": "<sup>12</sup> Bhowasi akabereka Obhedhi, Obhedhi akabereka Jese;",
			"13": "<sup>13</sup> Jese akabereka mwana wake wedangwe Eriabhi, tevere Abhinadhabhu wechipiri, naShimea wechitatu;",
			"14": "<sup>14</sup> naNetaneri wechina, naRadhai weshanu;",
			"15": "<sup>15</sup> naOzemi wetanhatu, naDhavhidhi wechinomwe.",
			"16": "<sup>16</sup> Hanzvadzi dzavo dzaiva Zeruya naAbhigairi. Vanakomana vaZeruya: Abhishai, naJoabhu, naAsaheri, vatatu.",
			"17": "<sup>17</sup> Abhigairi akabereka Amasa; baba vaAmasa vaiva Jeteri muIshimaeri.",
			"18": "<sup>18</sup> Karebhu mwanakomana waHezironi akaberekerwa vana nomukadzi wake Azubha, uye naJerioti; ava ndivo vaiva vanakomana vake: Jesheri, naShobhabhi, naAridhoni.",
			"19": "<sup>19</sup> Azubha akafa, Karebhu akawana Efurata, wakamuberekera Huri.",
			"20": "<sup>20</sup> Huri akabereka Uri, Uri akabereka Bhezari.",
			"21": "<sup>21</sup> Shure kwaizvozvo Hezironi akapinda kumukunda waMakiri baba vaGiriyadhi, waakawana asvika makore ana makumi matanhatu; iye akamuberekera Segubhi.",
			"22": "<sup>22</sup> Segubhi akabereka Jairi, akanga ana maguta ana makumi maviri namatatu panyika yeGiriyadhi.",
			"23": "<sup>23</sup> Geshuri naArami vakatorera vaJairi maguta avo, pamwechete neKenati nemisha yaro, maguta ana makumi matanhatu. Ava vose vaiva vanakomana vaMakiri baba vaGiriyadhi.",
			"24": "<sup>24</sup> Zvino Hezironi akati afa paKarebhiefirata, Abhija mukadzi waHezironi akamuberekera Ashuri baba vaTekoa.",
			"25": "<sup>25</sup> Vanakomana vaJerameeri, mwanakomana wedangwe waHezironi, vaiva, Rami wedangwe, naBhuna, naOreni, naOzemi, naAhija.",
			"26": "<sup>26</sup> Zvino Jerameeri akanga anomumwe mukadzi, ainzi Atara; iye akanga ari mai vaOnami.",
			"27": "<sup>27</sup> Vanakomana vaRami, wedangwe waJerameeri, vaiva Maazi, naJamini, naEkeri.",
			"28": "<sup>28</sup> Vanakomana vaOnami vaiva Shamai, naJadha. Vanakomana vaShamai: Nadhabhi naAbhishuri.",
			"29": "<sup>29</sup> Zita romukadzi waAbhishuri raiva Abhihairi; iye akamuberekera Abhani, naMoridhi.",
			"30": "<sup>30</sup> Vanakomana vaNadhabhi: Seredhi, naApaimi; asi Seredhi akafa asina vana.",
			"31": "<sup>31</sup> Vanakomana vaApaimi: Ishi. Vanakomana vaIshi: Sheshani. Vanakomana vaSheshani: Arai.",
			"32": "<sup>32</sup> Vanakomana vaJadha, munun'una waShamai: Jeteri, naJonatani; Jeteri akafa asina vana.",
			"33": "<sup>33</sup> Vanakomana vaJonatani: Pereti naZaza. Ndivo vaiva vanakomana vaJerameeri.",
			"34": "<sup>34</sup> Zvino Sheshani akanga asina vanakomana, asi vanasikana. Sheshani akanga ano muranda, muIjipiti, ainzi Jara.",
			"35": "<sup>35</sup> Sheshani akapa Jara muranda wake mukunda wake ave mukadzi wake; iye akamuberekera Atai.",
			"36": "<sup>36</sup> Atai akabereka Natani, Natani akabereka Zabhadhi;",
			"37": "<sup>37</sup> Zabhadhi akabereka Efirari, Efirari akabereka Obhedhi;",
			"38": "<sup>38</sup> Obhedhi akabereka Jehu, Jehu akabereka Azaria;",
			"39": "<sup>39</sup> Azaria akabereka Herezi, Herezi akabereka Ereasa;",
			"40": "<sup>40</sup> Ereasa akabereka Sisimai, Sisimai akabereka Sharumi;",
			"41": "<sup>41</sup> Sharumi akabereka Jekamia, Jekamia akabereka Erishama.",
			"42": "<sup>42</sup> Vanakomana vaKarebhu munun'una waJerameeri, vaiva Mesha dangwe rake, akanga ari baba vaZifi; navanakomana vaMaresha baba vaHebhuroni.",
			"43": "<sup>43</sup> Vanakomana vaHebhuroni: Kora, naTapua, naRekemi, naShema.",
			"44": "<sup>44</sup> Shema akabereka Rahami, baba vaJorikeami; Rekemi akabereka Shamai.",
			"45": "<sup>45</sup> Mwanakomana waShamai akanga ari Maoni; Maoni akanga ari baba vaBhetizuri.",
			"46": "<sup>46</sup> Efa murongo waKarebhu, akabereka Harani, naMoza, naGazezi; Harami akabereka Gazezi.",
			"47": "<sup>47</sup> Vanakomana vaJadha: Regemi, naJotamu, naGeshani, naPereti, naEfa, naShaafi.",
			"48": "<sup>48</sup> Maaka, murongo waKarebhu, akabereka Shebheri naTirihana.",
			"49": "<sup>49</sup> Iye akaberekawo Shaafi baba vaMadhimana, Shevha baba vaMakibhena, nababa vaGibhiya; mukunda waKarebhu akanga ari Akisa.",
			"50": "<sup>50</sup> Ndivo vanakomana vaKarebhu: Mwanakomana waHuri, wedangwe waEfurata: Shobhari baba vaKiriati-jearimi;",
			"51": "<sup>51</sup> Sarima baba vaBheterehemu, Harefi baba vaBhetigadheri.",
			"52": "<sup>52</sup> Shobhari baba vaKiriati-jearimi akanga ana vanakomana: Haroe, nehafu yorudzi rwavaMenuhoti.",
			"53": "<sup>53</sup> Marudzi aKiriati-jearimi: Valtiri, navaPuti, navaShumati, navaMishirai; kwavari ndiko kwakabva vaZorati navaEshitaori.",
			"54": "<sup>54</sup> Vanakomana vaSarima: Bheterehemu, navaNetofati, Atirotibhetijoabhi, nehafu yorudzi rwavaManahati, navaZori.",
			"55": "<sup>55</sup> Uye marudzi avanyori, aigara paJabhezi: VaTirati, vaShimeati, navaSukati. Ndivo vaKeni vakabva kuna Hamati baba veimba yaRekabhi."
		},
		"3":
		{
			"1": "<sup>1</sup> Ava ndivo vanakomana vaDhavhidhi, vakamuberekerwa paHebhuroni: Wedangwe Amunoni, mwana waAhinowami muJezireeri; wechipiri Dhanyeri waAbhigairi muKarimeri;",
			"2": "<sup>2</sup> wechitatu Abhusaromu mwanakomana waMaaka, mukunda waTarimai mambo weGeshuri; wechina Adhonia mwanakomana waHagiti;",
			"3": "<sup>3</sup> weshanu Shefatia waAbhitari, wetanhatu Itireami kuna Egira mukadzi wake.",
			"4": "<sup>4</sup> Wakaberekerwa vatanhatu paHebhuroni, akabata ushepo makore manomwe nemwedzi mitanhatu; akabata ushe paJerusaremu makore makumi matatu namatatu.",
			"5": "<sup>5</sup> Ava ndivo vaakaberekerwa paJerusaremu: Shimea, naShobhabhi, naNatani, naSoromoni, ivo vana, vaBhatishua mukunda waAmieri;",
			"6": "<sup>6</sup> naIbhari, naErishama, naErifereti;",
			"7": "<sup>7</sup> naNoga, naNefegi, naJafia;",
			"8": "<sup>8</sup> naErishama, naEriadha, naErifereti, ivo vapfumbamwe.",
			"9": "<sup>9</sup> Ndivo vose vaiva vana vaDhavhidhi, vanakomana vavarongo vasingaverengwi; uye Tamari wakanga ari hanzvadzi yavo.",
			"10": "<sup>10</sup> Mwanakomana waSoromoni akanga ari Rehobhoami, mwanakomana wake Abhia, mwanakomana wake Asa, mwanakomana wake Jehoshafati;",
			"11": "<sup>11</sup> mwanakomana wake Joramu, mwanakomana wake Ahazia, mwanakomana wake Joashi;",
			"12": "<sup>12</sup> mwanakomana wake Amazia, mwanakomana wake Azaria, mwanakomana wake Jotamu;",
			"13": "<sup>13</sup> mwanakomana wake Ahazi, mwanakomana wake Hezekia, mwanakomana wake Manase;",
			"14": "<sup>14</sup> mwanakomana wake Amoni, mwanakomana wake Josiya.",
			"15": "<sup>15</sup> Vanakomana vaJosiya: Wedangwe Johwanani, wechipiri Jehoyakimu, wechitatu Zedhekia, wechina Sharumi.",
			"16": "<sup>16</sup> Vanakomana vaJehoyakimu: Mwanakomana wake Jekonia, mwanakomana wake Zedhekia.",
			"17": "<sup>17</sup> Vanakomana vaJekonia, vakatapwa. Shearitieri mwanakomana wake,",
			"18": "<sup>18</sup> naMarikirami, naPedhaya, naShenazari, Jekamia, Hoshama, naNedhabhia.",
			"19": "<sup>19</sup> Vanakomana vaPedhaya: Zerubhabheri, naShimei; vanakomana vaZerubhabheri: Meshurami, naHanania; uye Sheromiti akanga ari hanzvadzi yavo;",
			"20": "<sup>20</sup> naHashubha, naOheri, naBherekia, naHasadhia, naJushabhiheshedhi, ivo vashanu.",
			"21": "<sup>21</sup> Vanakomana vaHanania: Peratia, naJeshaya; vanakomana vaRefaya, vanakomana vaArinani, vanakomana vaObhadhiya, vanakomana vaShekania.",
			"22": "<sup>22</sup> Vanakomana vaShekania: Shemaya; navanakomana vaShemaya; Hatushi, naIgari, naBharia, naNearia, naShafati, ivo vatanhatu.",
			"23": "<sup>23</sup> Vanakomana vaNearia: Erioenai, naHizikia, naAzirikami, ivo vatatu.",
			"24": "<sup>24</sup> Vanakomana vaErioenai: Hodhavhia, naEriashibhi, naPeraya, naAkubhi, naJohwanani, naDheraya, naAnani, ivo vanomwe."
		},
		"4":
		{
			"1": "<sup>1</sup> Vanakomana vaJudha: Perezi, naHezironi, naKarimi, naHuri, naShobhari.",
			"2": "<sup>2</sup> Reaya mwanakomana waShobhari akabereka Jahati, Jahati akabereka Ahumai naRahadhi. Ndiwo marudzi avaZorati.",
			"3": "<sup>3</sup> Uye ava ndivo vaiva vanakomana vababa vaEtami: Jezireeri, naIshima, naIdhibhashi; uye zita rehanzvadzi yavo rakanga riri Hazereponi;",
			"4": "<sup>4</sup> naPenueri baba vaGedhori, naEzeri baba vaHusha. Ndivo vana vaHuri, wedangwe waEfurata, baba vaBheterehemu.",
			"5": "<sup>5</sup> Zvino Ashuri baba vaTekoa akanga ana vakadzi vaviri, Hera naNaara.",
			"6": "<sup>6</sup> Naara akamuberekera Ahuzami, naHeferi, naTemeni, naHaahashitari. Ndivo vaiva vanakomana vaNaara.",
			"7": "<sup>7</sup> Vanakomana vaHera vaiva Zereti, naIzhari, naEtinani.",
			"8": "<sup>8</sup> Hakozi akabereka Anubhi, naZobhebha, nehama dzaAhareri mwanakomana waHarumi.",
			"9": "<sup>9</sup> Jabhezi akakudzwa kukunda vanun'una vake, mai vake vakamutumidza zita rinonzi Jabhezi, vachiti, \"Nokuti ndakamubereka ndichitambudzika.",
			"10": "<sup>10</sup> Jabhezi akadana kuna Mwari waIsiraeri, akati, \"Haiwa, dai muchindiropafadza henyu, nokukurisa nyika yangu, nokundifambisa noruoko rwenyu, nokundichengeta kune zvakaipa, kuti zvirege kundiwira!\" Mwari akamupa zvaakakumbira.",
			"11": "<sup>11</sup> Kerubhi munun'una waShuha akabereka Mehiri, iye akanga ari baba vaEshitoni.",
			"12": "<sup>12</sup> Eshitoni akabereka Bhetirafa, naPasea, naTehina baba vaIrinahashi. Ndivo vana vaReka.",
			"13": "<sup>13</sup> Vanakomana vaKenazi : Otineri, naSeraya; vanakomana vaOtineri: Hatati.",
			"14": "<sup>14</sup> Meonotai akabereka Ofira; Seraya akabereka Joabhu baba vaGeharashimi; nokuti dzaiva mhizha.",
			"15": "<sup>15</sup> Vanakomana vaKarebhu mwanakomana waJefune: Iru, naEra, naNaami; vanakomana vaEra: Kenazi.",
			"16": "<sup>16</sup> Vanakomana vaJehareri: Zifi, naZifa, naTiria, naAsareri.",
			"17": "<sup>17</sup> Vanakomana vaEzira: Jeteri, naMeredhi, naEferi, naJaroni. Bhitia, mukunda waFarao, akabereka Miriami, naShamai, naIshabha baba vaEshitemoa.",
			"18": "<sup>18</sup> Mukadzi wake muJudha akabereka Jeredhi baba vaGedhori, naHebheri baba vaSoko, naJekutieri baba vaZanowa. Ndivo vanakomana vaBhitia mukunda waFarao, akanga akawanikwa naMeredhi.",
			"19": "<sup>19</sup> Vanakomana vomukadzi waHodhia, hanzvadzi yaNahamu vakanga vari baba vaKeira muGarimi, naEshitemba muMaakati.",
			"20": "<sup>20</sup> Vanakomana vaShimoni: Amunoni, naRina, naBhenihanani, naTironi. Vanakomana vaIshi; Zoheti, naBhenizoheti.",
			"21": "<sup>21</sup> Vanakomana vaShera mwanakomana waJudha: Eri baba vaReka, naRaadha baba vaMaresha, nehama yedzimba dzaivo vairuka mucheka wakaisvonaka, veimba yaAshibhiya;",
			"22": "<sup>22</sup> naJokimi, navarume veKozebha, naJoashi, naSarafi, vaibata ushe hwaMoabhi, naJashubhirehemi. Mashoko awa ndeekare.",
			"23": "<sup>23</sup> Ndivo vaiva vaumbi vehari, uye vaigara paNetaimi neGedhera; ndipo pavaigara namambo kuzobata basa rake.",
			"24": "<sup>24</sup> Vanakomana vaSimiyoni: Nemueri, naJamini, naJaribhi, naZera, naShauri;",
			"25": "<sup>25</sup> mwanakomana wake Sharumi, mwanakomana wake Mibhisami, mwanakomana wake Mishima.",
			"26": "<sup>26</sup> Vanakomana vaMishima: Mwanakomana wake Haumueri, mwanakomana wake Zakuri, mwanakomana wake Shimei.",
			"27": "<sup>27</sup> Zvino Shimei akanga ana vanakomana vatanhatu; asi vanun'una vake vakanga vasina vana vazhinji, uye rudzi rwavo rwose haruna kuwanda, savana vaJudha.",
			"28": "<sup>28</sup> Ivo vakagara paBheerishebha, neMoradha, naHazarishuari;",
			"29": "<sup>29</sup> napaBhiriha, napaEzemi, napaToradhi;",
			"30": "<sup>30</sup> napaBhetueri, napaHorima, napaZikiragi;",
			"31": "<sup>31</sup> napaBhetimarikabhoti, napaHazarisusimi napaBhetibhiri, napaShaaraimi. Ndiwo aiva maguta avo kusvikira Dhavhidhi achibata ushe.",
			"32": "<sup>32</sup> Misha yavo yakanga iri Etami, neAini, neRimoni, neTokeni, neAshani, maguta mashanu;",
			"33": "<sup>33</sup> nemisha yavo yose, yakanga yakakomba maguta iwayo, kusvikira paBhaari. Ndipo pavaigara, uye mazita amarudzi avo aripo.",
			"34": "<sup>34</sup> NaMeshobhabhi, naJamireki, naJosha mwanakomana waAmazia;",
			"35": "<sup>35</sup> naJoseri, naJehu mwanakomana waJoshibhia, mwanakomana waSeraya, mwanakomana waAsieri;",
			"36": "<sup>36</sup> naErioenai, naJaakobha, naJeshohaya, naAsaya, naAdhieri, naJeshimieri, naBhenaya;",
			"37": "<sup>37</sup> naZiza mwanakomana waShifi, mwanakomana waAroni, mwanakomana waJedheiya, mwanakomana waShimiri, mwanakomana waShemaya;",
			"38": "<sup>38</sup> ava vakarehwa mazita avo, vaiva machinda pamarudzi avo; dzimba dzamadzibaba avo dzikawanda kwazvo.",
			"39": "<sup>39</sup> Vakaenda pavanopinda paGedhori, kusvikira parutivi rwamabvazuva rwomupata, kutsvakira makwai avo mafuro.",
			"40": "<sup>40</sup> Vakawana mafuro akanaka kwazvo, uye nyika yakanga iri mhami, ino rugare nokunyarara; nokuti vaigarapo kare vaiva vana vaHamu.",
			"41": "<sup>41</sup> Ava, vakanyorwa mazita avo, vakasvika pamazuva aHezekia mambo waJudha, vakaputsa matende avo uye vaMeunimi vakavawanapo, vakavaparadza chose, kusvikira nhasi, vakagara panyika yavo; nokuti kwakanga kuna mafuropo amakwai avo.",
			"42": "<sup>42</sup> Vamwe vavo, ivo vanakomana vaSimiyoni, vaiva varume vana mazana mashanu, vakaenda kugomo reSeiri, vakatungamirirwa naPeratia, naNearia, naRefaia, naUzieri, vanakomana valshi.",
			"43": "<sup>43</sup> Vakakunda vakanga vasara vavaAmareki, vakapukunyuka, vakagarapo kusvikira zuva ranhasi."
		},
		"5":
		{
			"1": "<sup>1</sup> Vana vaRubheni mwana wedangwe walsiraeri, (nokuti ndiye akanga ari wedangwe; asi zvaakasvibisa nhovo yababa vake, udangwe hwake hwakapiwa vanakomana vaJosefa mwanakomana waIsiraeri; naizvozvo mazita amadzitateguru haaverengwi nohudangwe.",
			"2": "<sup>2</sup> Nokuti Judha ndiye akanga ari mukuru pakati pavana vababa vake, mutungamiriri akabva kwaari; asi udangwe hwakanga huri hwaJosefa;)",
			"3": "<sup>3</sup> Vanakomana vaRubheni mwana wedangwe waIsiraeri: Hanoki, naParu, naHezironi, naKarimi.",
			"4": "<sup>4</sup> Vanakomana vaJoeri: Shemaya mwanakomana wake, Gogi mwanakomana wake, naShimei mwanakomana wake;",
			"5": "<sup>5</sup> Mika mwanakomana wake, naReaya mwanakomana wake, naBhaari mwanakomana wake;",
			"6": "<sup>6</sup> naBheera mwanakomana wake, iye akatapwa naTirigatipirineseri mambo weAsiria; iye akanga ari muchinda wavaRubheni.",
			"7": "<sup>7</sup> Navanun'una vake nedzimba dzavo kana mazita amadziteteguru achiverengwa: Mukuru Jeyieri, naZekariya,",
			"8": "<sup>8</sup> naBhera mwanakomana waAzazi, mwanakomana waShema, mwanakomana waJoeri, aigara paAroeri kusvikira paNebho neBhaarimeoni;",
			"9": "<sup>9</sup> kurutivi rwamabvazuva akanga agere kusvikira pakutanga kwerenje kubva kurwizi Yufuratesi; nokuti mombe dzavo dzakanga dzawanda panyika yeGiriyadhi.",
			"10": "<sup>10</sup> Namazuva aSauro vakarwa navaHagari, vakavakunda; vakagara pamatende avo panyika yose yakanga iri kumabvazuva eGiriyadhi.",
			"11": "<sup>11</sup> Vanakomana vaGadhi vakanga vagere kwakatarisana navo panyika yeBhashani, kusvikira paSareka;",
			"12": "<sup>12</sup> Joeri mukuru, naShafani wechipiri, naJanai, naShafati, paBhashani;",
			"13": "<sup>13</sup> vanun'una vavo vedzimba dzamadzibaba avo: Mikaeri, naMeshurami, naShebha, naJorai, naJakani, naZiya, naEbheri, ivo vanomwe.",
			"14": "<sup>14</sup> Ndivo vanakomana vaAbhihairi mwanakomana waHuri, mwanakomana waJaroa, mwanakomana waGiriyadhi, mwanakomana waMikaeri, mwanakomana waJeshishai, mwanakomana waJadho, mwanakomana waBhuzi,",
			"15": "<sup>15</sup> naAhi, mwanakomana waAbhidhieri, mwanakomana waGuni, mukuru wedzimba dzamadzibaba avo.",
			"16": "<sup>16</sup> Ivo vakanga vagere paGiriyadhi paBhashani, nomumisha yaro mikuru, nomuruwa rweSharoni, kusvikira kumiganhu yayo.",
			"17": "<sup>17</sup> Ava vose vaiverengwa namazita amadzitateguru avo pamazuva aJotamu mambo waJudha, napamazuva aJerobhoamu mambo waIsiraeri.",
			"18": "<sup>18</sup> Vanakomana vaRubheni, navaGadhi, nehafu yorudzi rwaManase, varume voumhare, varume vaigona kushonga nhovo neminondo, nokupfura nouta, vaigona kurwa, vakasvika zviuru zvina makumi manomwe namakumi matanhatu, vaigona kundorwa.",
			"19": "<sup>19</sup> Vakarwa vanaHagari, naJeturi, naNafishi, naHobhabhi.",
			"20": "<sup>20</sup> Vakabatsirwa pakurwa navo, vaHagari vakakundwa navo, navose vaiva navo; nokuti vakadana kuna Jehovha vari pakurwa, akavanzwa; nokuti vakanga vachitenda kwaari.",
			"21": "<sup>21</sup> Vakatapa zvipfuwo zvavo, makamera ane zviuru zvina makumi mashanu, namakwai ane zviuru zvina mazana maviri namakumi mashanu, nembongoro dzine zviuru zviviri, navanhu vane zviuru zvine zana.",
			"22": "<sup>22</sup> Vazhinji vakafa vaurawa, nokuti kurwa kwanga kuri kwaMwari. Ivo vakagara panzvimbo yavo kusvikira pakutapwa kwavo.",
			"23": "<sup>23</sup> Vanakomana vehafu yorudzi rwaManase vakanga vagere panyika iyo; vakawanda kubva paBhashani kusvikira paBhaariherimoni neSeniri, negomo reHerimoni.",
			"24": "<sup>24</sup> Vakuru vedzimba dzamadzibaba avo ndivo: Eferi, nalshi, naErieri, naAshirieri, naJeremiya; naHodhavhia, naJadhieri, varume vaiva nesimba noumhare, vanhu vaiva nembiri, vakuru vedzimba dzamadzibaba avo.",
			"25": "<sup>25</sup> Asi vakazotadzira Mwari wamadzibaba avo, vakapata vachitevera vamwari vendudzi dzenyika iyo, dzakanga dzaparadzwa naMwari pamberi pavo.",
			"26": "<sup>26</sup> Mwari walsiraeri akamutsa mweya waPuri mambo waAsiria, nomweya waTirigatipirineseri mambo weAsiria; iye akavatapa, ivo vaRubheni, navaGadhi, nehafu yorudzi rwaManase, akavaisa Hara, neHabhori, neHara, nokurwizi Gozani kusvikira nhasi."
		},
		"6":
		{
			"1": "<sup>1</sup> Vanakomana vaRevhi: Gerishoni, naKohati, naMerari;",
			"2": "<sup>2</sup> navanakomana vaKohati: Amirami, naIshari, naHebhuroni, naUzieri,",
			"3": "<sup>3</sup> Vanakomana vaAmirami: Aroni, naMozisi, naMiriami. Vanakomana vaAroni: Nadhabhi, naAbhihu, naEriazari, naltamari.",
			"4": "<sup>4</sup> Eriazari akabereka Pinehasi, Pinehasi akabereka Abhishua,",
			"5": "<sup>5</sup> Abhishua akabereka Bhuki, Bhuki akabereka Uzi,",
			"6": "<sup>6</sup> Uzi akabereka Zerahiya, Zerahiya akabereka Merayoti,",
			"7": "<sup>7</sup> Merayoti akabereka Amaria, Amaria akabereka Ahitubhi,",
			"8": "<sup>8</sup> Ahitubhi akabereka Zadhoki, Zadhoki akabereka Ahimaazi,",
			"9": "<sup>9</sup> Ahimaazi akabereka Azaria, Azaria akabereka Johwanani,",
			"10": "<sup>10</sup> Johwanani akabereka Azaria, (ndiye waibata basa romuprista muimba yakavakwa naSoromoni paJerusaremu;)",
			"11": "<sup>11</sup> Azaria akabereka Amaria, Amaria akabereka Ahitubhi,",
			"12": "<sup>12</sup> Ahitubhi akabereka Zadhoki, Zadhoki akabereka Sharumi,",
			"13": "<sup>13</sup> Sharumi akabereka Hirikia, Hirikia akabereka Azaria,",
			"14": "<sup>14</sup> Azaria akabereka Seraya, Seraya akabereka Jehozadhaki,",
			"15": "<sup>15</sup> Jehozadhaki akatapwa, nguva yakatapa Jehovha Judha neJerusaremu noruoko rwaNebhukadhinezari.",
			"16": "<sup>16</sup> Vanakomana vaRevhi, Gerishomi, naKohati, naMerari.",
			"17": "<sup>17</sup> Mazita avanakomana vaGerishomi ndiwo: Ribhini naShimei.",
			"18": "<sup>18</sup> Vanakomana vaKohati: Amirami, naIshari, naHebhuroni, naUzieri;",
			"19": "<sup>19</sup> navanakomana vaMerari: Mari, naMushi. Uye ndidzo dzimba dzavaRevhi, nedzimba dzamadzibaba avo.",
			"20": "<sup>20</sup> VaGerishoni: Ribhini mwanakomana wake, naJahati mwanakomana wake, naZima mwanakomana wake,",
			"21": "<sup>21</sup> naJoa mwanakomana wake, naIdho mwanakomana wake, naZera mwanakomana wake, naJeaterai mwanakomana wake.",
			"22": "<sup>22</sup> Vanakomana vaKohati: Aminadhabhu mwanakomana wake, naKora mwanakomana wake, naAsiri mwanakomana wake,",
			"23": "<sup>23</sup> naErikana mwanakomana wake, naAbhiasafit mwanakomana wake, naAsiri mwanakomana wake,",
			"24": "<sup>24</sup> naTahati mwanakomana wake, naUrieri mwanakomana wake, naUzi mwanakomana wake, naShauri mwanakomana wake.",
			"25": "<sup>25</sup> Vanakomana vaErikana: Amasai, naAhimoti.",
			"26": "<sup>26</sup> Vanakomana vaErikana: Zofai mwanakomana wake, naNahati mwanakomana wake,",
			"27": "<sup>27</sup> naEriabhi mwanakomana wake, naErikana mwanakomana wake.",
			"28": "<sup>28</sup> Vanakomana vaSamueri: wedangwe Joeri, nowechipiri Abhija.",
			"29": "<sup>29</sup> Vanakomana vaMerari: Mari, naRibhini mwanakomana wake, naUza mwanakomana wake,",
			"30": "<sup>30</sup> naShimira mwanakomana wake, naHagia mwanakomana wake, naAsaya mwanakomana wake.",
			"31": "<sup>31</sup> Uye ava ndivo vakagadzwa naDhavhidhi kuzofambisa basa rokuimba mumba maJehovha, mushure mokuti areka yawana pokuzorora.",
			"32": "<sup>32</sup> Vaishumira pakuimba pamberi petabhenekari yetende rokusangana, kusvikira Soromoni avaka imba yaJehovha paJerusaremu; vakamira pabasa ravo, sezvavakanga vagoverwa napo.",
			"33": "<sup>33</sup> Ndivo vaimirapo navakomana vavo; kuvanakomana vaKohati: Hemani muimbi, mwanakomana waJoeri, mwanakomana waSamueri;",
			"34": "<sup>34</sup> mwanakomana waErikana, mwanakomana waJehorami, mwanakomana waErieri, mwanakomana waToa;",
			"35": "<sup>35</sup> mwanakomana waZufi, mwanakomana waErikana, mwanakomana waMahati, mwanakomana waAmasai,",
			"36": "<sup>36</sup> mwanakomana waErikana, mwanakomana waJoeri, mwanakomana waAzaria, mwanakomana waZefaniya,",
			"37": "<sup>37</sup> mwanakomana waTahati, mwanakomana waAsiri, mwanakomana waEbhiasafi, mwanakomana waKora;",
			"38": "<sup>38</sup> mwanakomana waIshari, mwanakomana waKohati, mwanakomana waRevhi, mwanakomana waIsiraeri.",
			"39": "<sup>39</sup> Munun'una wake Asafi, akanga amire kurudyi rwake, iye Asafi mwanakomana waBherekia, mwanakomana waShimea;",
			"40": "<sup>40</sup> mwanakomana waMikaeri, mwanakomana waBhaaseya, mwanakomana waMarikija;",
			"41": "<sup>41</sup> mwanakomana waItini, mwanakomana waZera, mwanakomana waAdhaya;",
			"42": "<sup>42</sup> mwanakomana waEtani, mwanakomana waZima, mwanakomana waShimeri;",
			"43": "<sup>43</sup> mwanakomana waJahati, mwanakomana waGerishomi, mwanakomana waRevhi.",
			"44": "<sup>44</sup> Kurubhosbwe rwavo, hama dzavo ivo vanakomana vaMerari: Etani mwanakomana waKishi, mwanakomana waAbhidhi, mwanakomana waMaruki;",
			"45": "<sup>45</sup> mwanakomana waHashabhia, mwanakomana waAmazia, mwanakomana waHirikia;",
			"46": "<sup>46</sup> mwanakomana waAmuzi, mwanakomana waBhani, mwanakomana waShemeri;",
			"47": "<sup>47</sup> mwanakomana waMari, mwanakomana waMushi, mwanakomana waMerari, mwanakomana waRevhi.",
			"48": "<sup>48</sup> Hama dzavo ivo vaRevhi ndivo vakagoverwa mabasa ose patabhenekari yeimba yaJehovha.",
			"49": "<sup>49</sup> Asi Aroni navanakomana vake vaibayira paaritari yezvipiriso zvinopiswa, napaaritari yezvinonhuhwira, vaibata mabasa ose apanzvimbo tsvene-tsvene, nokuyananisira Isiraeri, vachiita zvose zvakarairwa naMozisi muranda waMwari.",
			"50": "<sup>50</sup> Vanakomana vaAroni ndivo: Eriazari mwanakomana wake, naPinehasi mwanakomana wake, naAbhishua mwanakomana wake;",
			"51": "<sup>51</sup> naBhuki mwanakomana wake, naUzi mwanakomana wake, naZerahiya mwanakomana wake;",
			"52": "<sup>52</sup> naMerayoti mwanakomana wake, naAmaria mwanakomana wake, naAhitubhi mwanakomana wake;",
			"53": "<sup>53</sup> naZadhoki mwanakomana wake, naAhimaazi mwanakomana wake.",
			"54": "<sup>54</sup> Kwavaigara, kwavaidzika matende avo panyika yavo, ndiko, vanakomana waAroni, vedzimba dzavaKohati, nokuti ndivo vakatanga kugoverwa,",
			"55": "<sup>55</sup> ivo vakapiwa Hebhuroni panyika yaJudha, namafuro aro akanga akapoteredza;",
			"56": "<sup>56</sup> asi minda yeguta, nemisha yaro, yakapiwa Karebhu mwanakomana waJefune.",
			"57": "<sup>57</sup> Vanakomana vaAroni vakapiwa maguta outiziro, aiti: Hebhuroni, neRibhina namafuro awo, neJatiri, neEshitemoa namafuro awo;",
			"58": "<sup>58</sup> neHireni namafuro aro, neDhebhiri namafuro aro;",
			"59": "<sup>59</sup> neAshani namafuro aro, neBhetishemeshi namafuro aro.",
			"60": "<sup>60</sup> Uye vorudzi rwaBhenjamini: Gebha namafuro aro, neArimeti namafuro aro, neAnatoti namafuro aro. Maguta avo ose pakati pedzimba dzavo dzose, aiva maguta ane gumi namatatu.",
			"61": "<sup>61</sup> Vamwe vanakomana vaKohati vakapiwa nemijenya maguta ane gumi pakati pehafu yorudzi rwaManase, iyo hafu yaManase.",
			"62": "<sup>62</sup> Vanakomana vaGerishomi nedzimba dzavo, vorudzi rwaIsakari, novorudzi rwaAsheri, novorudzi rwaNafutari, novorudzi rwaManase paBhashani, vakapiwa maguta ane gumi namatatu.",
			"63": "<sup>63</sup> Vanakomana vaMerari nedzimba dzavo vakapiwa nemijenya vorudzi rwaRubheni, novorudzi rwaGadhi, novorudzi rwaZebhuruni, maguta ane gumi namaviri.",
			"64": "<sup>64</sup> Vana vaIsiraeri vakapa vaRevhi maguta namafuro awo.",
			"65": "<sup>65</sup> Vakavapa nemijenya maguta iwayo akarehwa mazita awo vorudzi rwavana vaJudha, novorudzi rwavana vaSimiyoni, novorudzi rwavana vaBhenjamini.",
			"66": "<sup>66</sup> Asi vamwe vedzimba dzavana vaKohati vakanga vana maguta panyika yavo, vorudzi rwaEfuremu.",
			"67": "<sup>67</sup> Vakavapa maguta outiziro, aiti: Shekemu kunyika yamakomo aEfuremu namafuro aro, neGezeri namafuro aro;",
			"68": "<sup>68</sup> neJokimeami namafuro aro, neBhetihoroni namafuro aro,",
			"69": "<sup>69</sup> neAjaroni namafuro aro, neGatirimoni namafuro aro;",
			"70": "<sup>70</sup> vehafu yorudzi rwaManase vakapiwa Aneri namafuro aro, ndizvo zvakapiwa vamwe veimba yavana vaKohati.",
			"71": "<sup>71</sup> Vanakomana vaGerishomi vakapiwa pakati peimba yehafu yorudzi rwaManase, Gorani paBhashani namafuro aro, neAshitaroti namafuro aro;",
			"72": "<sup>72</sup> novorudzi rwaIsakari: Kedheshi namafuro aro, neDhabherati namafuro aro,",
			"73": "<sup>73</sup> neRamoti namafuro aro, neAnemi namafuro aro;",
			"74": "<sup>74</sup> novorudzi rwaAsheri: Mashari namafuro aro, neAbhidhoni namafuro aro;",
			"75": "<sup>75</sup> neHukoki namafuro aro, neRehobhi namafuro aro,",
			"76": "<sup>76</sup> novorudzi rwaNafutari: Kedheshi paGarire namafuro aro, neHamoni namafuro aro, neKiriataimi namafuro aro.",
			"77": "<sup>77</sup> Vamwe vavaRevhi, ivo vanakomana vaMerari, vakapiwa vorudzi rwaZebhuruni: Rimono namafuro aro, neTabhori namafuro aro;",
			"78": "<sup>78</sup> uye mhiri kwaJorodhani paJeriko, kumabvazuva aJorodhani, vorudzi rwaRubheni, vakapiwa Bhezeri riri kurenje namafuro aro, neJaza namafuro aro,",
			"79": "<sup>79</sup> neKedhemoti namafuro aro, neMefati namafuro aro;",
			"80": "<sup>80</sup> novorudzi rwaGadhi: Ramoti paGiriyadhi namafuro aro, neMahanaimu namafuro aro,",
			"81": "<sup>81</sup> neHeshibhoni namafuro aro, neJazeri namafuro aro."
	    },
        "7":
        {
            "1": "<sup>1</sup> Vanakomana vaIsakari: Tora, naPua, naJashubhi, naShimironi, ivo vana.",
            "2": "<sup>2</sup> Vanakomana vaTora: Uzi, naRefaya, naJerieri, naJamai, naIbhisami, naShemueri, vakuru vedzimba dzamadzibaba avo, idzo dzaTora; vaiva varume vane simba noumhare pakati pamarudzi avo, pamazuva aDhavhidhi vakasvika zviuru zvina makumi maviri nezviviri namazana matanhatu.",
            "3": "<sup>3</sup> Vanakomana vaUzi; Izirahiya; navanakomana vaIzirahiya: Mikaeri naObhadhiya, naJoeri, naIshia, ivo vashanu; ava vose vakanga vari vakuru.",
            "4": "<sup>4</sup> Kwavari, kumarudzi avo nokudzimba dzamadzibaba avo, kwakanga kuna mapoka ehondo dzokurwa ane zviuru zvina makumi matatu nezvitanhatu; nokuti vakanga vana vakadzi vazhinji navanakomana vazhinji.",
            "5": "<sup>5</sup> Hama dzavo pakati pedzimba dzose dzaIsakari, ivo varume vane simba noumhare, vakasvika zviuru zvina makumi masere nezvinomwe, kana vachiverengwa mazita avo ose.",
            "6": "<sup>6</sup> Vanakomana vaBhenjamini: Bhera, naBhekeri, naJedhiyaeri, ivo vatatu.",
            "7": "<sup>7</sup> Vanakomana vaBhera: Ezibhoni, naUzi, naUzieri, naJerimoti, naIri, ivo vashanu. Ivo vakanga vari vakuru vedzimba dzamadzibaba avo, varume vane simba noumhare, vakaverengwa mazita avo vakasvika zviuru zvina makumi maviri nezviviri namakumi matatu navana.",
            "8": "<sup>8</sup> Vanakomana vaBhekeri: Zemira, naJoashi, naEriezeri, naErioenai, naOmiri, naJeremoti, naAbhiia, naAnatoti, naArimeti. Ivo vose vakanga vari vanakomana vaBhekeri.",
            "9": "<sup>9</sup> Vakaverengwa mazita avo namarudzi avo, vakuru vedzimba dzamadzibaba avo, varume vane simba noumhare, vakasvika zviuru zvina makumi maviri namazana maviri.",
            "10": "<sup>10</sup> Vanakomana vaJedhiyaeri: Bhirihani, navanakomana vaBhirihani; Jehushi, naBhenjamini, naEhudhi, naKenani, naZetani, naTarishishi, naAhishahari.",
            "11": "<sup>11</sup> Ivo vose vakanga vari vanakomana vaJedhiyaeri, vachiverengwa vakuru vedzimba dzamamdzibaba avo, varume vane simba noumhare, vakasvika zviuru zvine gumi nezvinomwe namazana maviri, vakanga vachigona kuenda kuhondo kundorwa.",
            "12": "<sup>12</sup> NaShupimiwo, naHupimi, vanakomana vaIri, naHushimi, vanakomana vaAheri.",
            "13": "<sup>13</sup> Vanakomana vaNafutari: Jazieri, naGuni, naJezeri, naSharumi, vanakomana vaBhiriha.",
            "14": "<sup>14</sup> Vanakomana vaManase: Asirieri, waakaberekerwa nomukadzi wake; (murongo wake muArami akabereka Makiri baba vaGiriyadhi;",
            "15": "<sup>15</sup> Makiri akawana mukadzi worudzi rwaHupimi naShupimi, zita rehanzvadzi yake raiva Maaka;) zita rowechipiri rakanga riri Zerofehadhi; Zerofehadhi akanga anavakunda.",
            "16": "<sup>16</sup> Maaka mukadzi waMakiri akabereka mwanakomana, akatumidza zita rake Pereshi; zita romunun'una wake raiva Shereshi, vanakomana vake vakanga vari Urami naRakemi.",
            "17": "<sup>17</sup> Vanakomana vaUrami: Bhedhani. Ndivo vanakomana vaGiriyadhi mwanakomana waMakiri, mwanakomana waManase.",
            "18": "<sup>18</sup> Hanzvadzi yake Hamoreketi akabereka Ishodhi, naAbhiezeri, naMara.",
            "19": "<sup>19</sup> Vanakomana vaShemidha: Ahiani, naShekemu, naRiki naAniami.",
            "20": "<sup>20</sup> Vanakomana vaEfuremu: Shutera, naBheredhi mwanakomana wake, naTahati mwanakomana wake, naEreadha mwanakomana wake, naTahati mwanakomana wake,",
            "21": "<sup>21</sup> naZabhadhi mwanakomana wake, naShutera mwanakomana wake, naEzeri, naEreadhi; ivo vakaurawa navanhu veGati vakanga vakaberekerwa panyika iyo, nokuti vakanga vaburukira kwavari kuzotora zvipfuwo zvavo.",
            "22": "<sup>22</sup> Efuremu baba vavo akavachema mazuva mazhinji, hama dzake dzikauya kuzomubata ruoko.",
            "23": "<sup>23</sup> Akapinda kumukadzi wake, iye akava nemimba, akabereka mwanakomana, akamutumidza zita rinonzi Bheriya; nokuti imba yake yakanga ichitambudzika.",
            "24": "<sup>24</sup> Mukunda wake waiva Sheera, iye akavaka Bhetihoroni rezasi nerokumusoro, neUzenisheera.",
            "25": "<sup>25</sup> Mwanakomana waEfuremu aiva Refa, naReshefi, naTera mwanakomana wake, naTahani mwanakomana wake;",
            "26": "<sup>26</sup> naRadhani mwanakomana wake, naErishama mwanakomana wake,",
            "27": "<sup>27</sup> naNuni mwanakomana wake, naJoshua mwanakomana wake.",
            "28": "<sup>28</sup> Nyika yavo nokwavakanga vagere ndiBhetieri nemisha yaro; nokumabvazuva, Naarani, nokumavirira Gezeri nemisha yaro; neShekemu nemisha yaro, kusvikira paAza nemisha yaro;",
            "29": "<sup>29</sup> nokumiganhu yavana vaManase. Bhetisheani nemisha yaro, neTaanaki nemisha yaro, neMegidho nemisha yaro, neDhori nemisha yaro. Vana vaJosefa mwanakomana waIsiraeri vakagara mumisha iyi.",
            "30": "<sup>30</sup> Vanakomana vaAsheri: Imina, naIshivha, naIshivhi, naBheriya, naSera hanzvadzi yavo.",
            "31": "<sup>31</sup> Vanakomana vaBheriya: Hebheri, naMarikieri, waiva baba vaBhirizaiti.",
            "32": "<sup>32</sup> Hebheri akabereka Jafureti, naShomeri, naHotami, naShua hanzvadzi yavo.",
            "33": "<sup>33</sup> Vanakomana Jafureti: Pasaki, naBhimari, naAshivati. Ndivo vanakomana vaJafureti.",
            "34": "<sup>34</sup> Vanakomana vaShemeri: Ahi, naRoga, naJehubha, naArami.",
            "35": "<sup>35</sup> Vanakomana vaHeremi munun'una wake: Zofa, naImina, naShereshi, naAmari.",
            "36": "<sup>36</sup> Vanakomana vaZofa: Shua, naHarineferi, naShuari, naBheri, naImira;",
            "37": "<sup>37</sup> naBheseri, naHodhi, naShama, naShirisha, naItirani, naBheera.",
            "38": "<sup>38</sup> Vanakomana vaJeteri: Jefune, naPisipa, naAra.",
            "39": "<sup>39</sup> Vanakomana vaUra: Ara, naHanieri, naRizia.",
            "40": "<sup>40</sup> Ava vose vaiva vana vaAsheri, vari vakuru vedzimba dzamadzibaba avo, varume vakanga vakatsaurwa, vane simba noumhare, vakuru vamachinda. Vakaverengwa namazita avo, ivo vaigona kurwa, vakasvika zviuru zvina makumi maviri nezvitanhatu."
        },
        "8":
        {
            "1": "<sup>1</sup> Bhenjamini akabereka mwana wake wedangwe Bhera, naAshibheri wechipiri, naAhara wechitatu;",
            "2": "<sup>2</sup> naNowa wechina, naRafa weshanu.",
            "3": "<sup>3</sup> Bhera akabereka vanakomana: Adhari, naGera naAbhihudhi;",
            "4": "<sup>4</sup> naAbhishua, naNaamani, naAhoa;",
            "5": "<sup>5</sup> naGera, naShefufani, naHurami.",
            "6": "<sup>6</sup> Ava ndivo vanakomana vaEhudhi: Ndivo vakuru vedzimba dzamadzibaba aigara paGebha, vakavatapa, vakaenda navo Manahati;",
            "7": "<sup>7</sup> naNaamani, naAhiya, naGera, iye akavatapa; akabereka Uza naAhihudhi.",
            "8": "<sup>8</sup> Shaharaimi akabereka vanakomana panyika yaMoabhi, adzinga vakadzi vake Hushimi naBhaara.",
            "9": "<sup>9</sup> Akabereka kumukadzi wake Hodheshi: Jobhabhu, naZibhia, naMesha, naMarikami;",
            "10": "<sup>10</sup> naJeuzi, naShakia, naMirima. Ndivo vanakomana vake, vakuru vedzimba dzamadzibaba.",
            "11": "<sup>11</sup> Hushimi akabereka Abhitubhi naEripaari.",
            "12": "<sup>12</sup> Vanakomana vaEripaari: Ebheri, naMishami, naShemedhi; ndiye wakavaka Ono, naRodhi, nemisha yawo;",
            "13": "<sup>13</sup> naBheira, naShema, ivo vaiva vakuru vedzimba dzamadzibaba aigara paAjaroni, vakadzinga vakanga vagere paGati;",
            "14": "<sup>14</sup> naAhiyo, naShashaki, naJeremoti;",
            "15": "<sup>15</sup> naZebhedhia, naAradhi, naEdheri;",
            "16": "<sup>16</sup> naMikaeri, naIshipa, naJoha, ivo vanakomana vaBheira;",
            "17": "<sup>17</sup> naZebhedhia, naMeshurami, naHiziki, naHebheri;",
            "18": "<sup>18</sup> naIshimerai, naIziria, naJobhabhu, ivo vanakomana vaEripaari;",
            "19": "<sup>19</sup> naJakimi, naZikiri, naZabhudhi;",
            "20": "<sup>20</sup> naErienai, naZiretai, naErieri;",
            "21": "<sup>21</sup> naAdhaya, naBheraya, naShimirati, ivo vanakomana vaShimei;",
            "22": "<sup>22</sup> naIshipani, naEbheri, naErieri;",
            "23": "<sup>23</sup> naAbhidhoni, naZikiri, naHanani;",
            "24": "<sup>24</sup> naHanania, naErami, naAnatotiya;",
            "25": "<sup>25</sup> naIfudheya, naPenueri, ivo vanakomana vaShashaki;",
            "26": "<sup>26</sup> naShamisherai, neSheharia, naAtaria;",
            "27": "<sup>27</sup> naJareshia, naEria, naZikiri, ivo vanakomana vaJehorami.",
            "28": "<sup>28</sup> Ivo vaiva vakuru vedzimba dzamadzibaba kumarudzi avo ose, vakuru; ivo vaigara Jerusaremu.",
            "29": "<sup>29</sup> PaGibhiyoni paigara baba vaGibhiyoni, wainzi Jeyieri, zita romukadzi wake rakanga riri Maaka.",
            "30": "<sup>30</sup> Mwanakomana wake wedangwe aiva Abhidhoni, naZuri, naKishi, naBhaari, naNadhabhi;",
            "31": "<sup>31</sup> naGedhori, naAhio, naZekariya.",
            "32": "<sup>32</sup> Mikiroti akabereka Shimea. Naivo vaigara nehama dzavo Jerusaremu, vakatarisana nehama dzavo.",
            "33": "<sup>33</sup> Neri akabereka Kishi, Kishi akabereka Sauro, Sauro akabereka Jonatani, naMarikishua, naAbhinadhabhu, naEshibhaari.",
            "34": "<sup>34</sup> Mwanakomana waJonatani akanga ari Meribhibhaari; Meribhibhaari akabereka Mika.",
            "35": "<sup>35</sup> Vanakomana vaMika: Pitoni, naMereki, naTarea, naAhazi.",
            "36": "<sup>36</sup> Ahazi akabereka Joadha, Joadha akabereka Arimeti, naAzimavheti, naZimiri; Zimiri akabereka Moza;",
            "37": "<sup>37</sup> Moza akabereka Bhinea; Rafa waiva mwanakomana wake, naEreasa mwanakomana wake, naAzeri mwanakomana wake.",
            "38": "<sup>38</sup> Azeri wakanga ana vanakomana vatanhatu; mazita avo ndiwo: Azirikami, naBhokeru, naIshimaeri, naShearia, naObhadhiya, naHanani. Ava vose vaiva vanakomana vaAzeri.",
            "39": "<sup>39</sup> Vanakomana vaEsheki munun'una wake: Urami mwanakomana wake wedangwe, naJeuzi wechipiri, naErifereti wechitatu.",
            "40": "<sup>40</sup> Vanakomana vaUrami vaiva varume vane simba noumhare, vapfuri vemiseve; vakanga vana vanakomana vazhinji, navanakomana vavanakomana vavo, vakasvika zana namakumi mashanu. Ivo vose vaiva vanakomana vaBhenjamini."
        },
        "9":
        {
            "1": "<sup>1</sup> Naizvozvo vaIsiraeri vose vakaverengwa mazita avo; tarirai, akanyorwa ose mubhuku yamadzimambo alsiraeri. VaJudha vakazotapwa, vakaiswa Bhabhironi nokuda kokudarika kwavo.",
            "2": "<sup>2</sup> Zvino, vakatanga kugara panyika yavo napamaguta avo, vakanga vari vaIsiraeri, navaprista, navaRevhi, navaNetinimi.",
            "3": "<sup>3</sup> PaJerusaremu ndipo pakanga pagere vana vaJudha, navana vaBhenjamini, navana vaEfuremu, navaManase;",
            "4": "<sup>4</sup> Utai mwanakomana waAmihudhi, mwanakomana waOmiri, mwanakomana waImiri, mwanakomana waBhani, wavana vaPerezi mwanakomana waJudha.",
            "5": "<sup>5</sup> NavaShiro: Asaya mwanakomana wedangwe, navanakomana vake.",
            "6": "<sup>6</sup> Vanakomana vaZera: Jeueri, nehama dzavo, vana mazana matanhatu namakumi mapfumbamwe.",
            "7": "<sup>7</sup> Vanakomana vaBhenjamini: Saru, mwanakomana waMeshirami, mwanakomana waHodhavhia, mwanakomana waHasenua.",
            "8": "<sup>8</sup> Nalbheneya, mwanakomana waJehorami, naEra mwanakomana waUzi, mwanakomana waMikiri, naMeshurami mwanakomana waShefatia, mwanakomana waReueri, mwanakomana waIbhiniya;",
            "9": "<sup>9</sup> nehama dzavo, marudzi avo aisvika mazana mapfumbamwe namakumi mashanu namatanhatu. Varume ava vose vakanga vari vakuru vedzimba dzamadzibaba padzimba dzamadzibaba avo.",
            "10": "<sup>10</sup> Vavaprista: Jedheiya, naJehoyaribhi, naJakini;",
            "11": "<sup>11</sup> naAzaria, mwanakomana waHirikia, mwanakomana waMeshurami, mwanakomana waZadhoki, mwanakomana waMerayoti, mwanakomana waAhitubhi, mukuru paimba yaMwari,",
            "12": "<sup>12</sup> naAdhaya, mwanakomana waJehorami, mwanakomana waPashuri, mwanakomana waMarikija, naMaasai, mwanakomana waAdhieri, mwanakomana waJazera, mwanakomana waMeshurami, mwanakomana waMeshiremiti, mwanakomana waImeri;",
            "13": "<sup>13</sup> nehama dzavo, vakuru vedzimba dzamadzibaba avo vakasvika chiuru chimwe namazana manomwe namakumi matanhatu; varume vane simba kwazvo noumhare pabasa rokushumira paimba yaMwari.",
            "14": "<sup>14</sup> VavaRevhi: Shemaya mwanakomana waHashubhi, mwanakomana waAzirikami, mwanakomana waHashabhia, wavanakomana vaMerari;",
            "15": "<sup>15</sup> naBhakibhakari, naHoreshi, naGarari, naMatania mwanakomma waMika, mwanakomana waZikiri, mwanakomana waAsafi;",
            "16": "<sup>16</sup> naObhadhiya mwanakomana waShemaya, mwanakomana waGarari, mwanakomana waAsa, mwanakomana waErikana, vakanga vagere pamisha yavaNetofati.",
            "17": "<sup>17</sup> Vatariri vamasuo: Sharumi, naAkubhi, naTarimoni, naAhikami, nehama dzavo; Sharumi ndiye akanga ari mukuru;",
            "18": "<sup>18</sup> ndivo vaitarira suo ramambo rokumabvazuva kusvikira zvino; vakanga vari vatariri vamasuo emisasa yavana vaRevhi.",
            "19": "<sup>19</sup> Sharumi mwanakomana waKore, mwanakomana waEbhiasafi, mwanakomana waKora, nehama dzake, ivo veimba yababa vake, vaKora, ndivo vaiva nebasa rokushumira, vakatarira masuo etabhenekari; madzibaba avo aitarira misasa yaJehovha, nokuchengeta mikova.",
            "20": "<sup>20</sup> Pinehasi mwanakomana waEriazari aiva murairi wavo kare, Jehovha akava naye.",
            "21": "<sup>21</sup> Zekariya mwanakomana waMesheremia, akanga ari mutariri womukova wetende rokusangana.",
            "22": "<sup>22</sup> Ava vose, vakatsaurwa kuti vave vatariri vamasuo, vakasvika mazana maviri negumi navaviri. Ava vose vakaverengwa mazita avo pamisha yavo, vakanga vakagadzwa pabasa ravo rakatarwa naDhavhidhi naSamueri muoni.",
            "23": "<sup>23</sup> Naizvozvo ivo navana vavo vakanga vari vatariri vamasuo eimba yaJehovha, iyo imba yetabhenekari, vachiravana.",
            "24": "<sup>24</sup> Vatariri vakanga vari kumativi mana, kumabvazuva, nokumavirira, nokumusoro, nenyasi.",
            "25": "<sup>25</sup> Hama dzavo pamisha yavo vaifanira kuuya nguva dzose kana mazuva manomwe apera, kuzogara navo.",
            "26": "<sup>26</sup> Nokuti vatariri vana vakuru vemikova, vakanga vari vaRevhi, vaiva pabasa iri rakatarwa, vaitarira makamuri nezvivigiro zvefuma paimba yaMwari.",
            "27": "<sup>27</sup> Vaigarapo vakapoteredza imba yaMwari, nokuti ndivo vakanga vapiwa basa iri, vaiva nebasa rokuizarura mangwanani ose.",
            "28": "<sup>28</sup> Vamwe vavo vaifanira kutarira midziyo yokushumira nayo; nokuti yaipinzwa ichiverengwa, uye yaibudiswa ichiverengwa.",
            "29": "<sup>29</sup> Vamwe vavozve vakanga vakagadzwa kuti vatarire nhumbi nemidziyo yose nenzvimbo tsvene, noupfu hwakatsetseka, newaini, namafuta, nezvinonhuhwira, nemiti inonhuhwira.",
            "30": "<sup>30</sup> Vamwezve vavanakomana vavaprista vaifanira kugadzira zvinonhuhwira zvakavenganiswa.",
            "31": "<sup>31</sup> Matitia, mumwe wavaRevhi, mwanakomana wedangwe waSharumi, wavaKora, wakanga akatarirwa basa rezvinhu zvaibikwa mugango.",
            "32": "<sup>32</sup> Navamwe vehama dzavo, vavana vaKora, vaifanira kutarira zvingwa zvokuratidza, kuzvigadzira Sabata rimwe nerimwe.",
            "33": "<sup>33</sup> Uye ndivo vaimbi, vakuru vedzimba dzamadzibaba avaRevhi, vaigara mumakamuri vasingabati zvimwe; nokuti vaibata basa ravo masikati nousiku.",
            "34": "<sup>34</sup> Ivo vaiva vakuru vedzimba dzamadzibaba avaRevhi, vakuru kumarudzi avo; ndivo vaigara Jerusaremu.",
            "35": "<sup>35</sup> Zvino paGibhiyoni pakanga pagere baba vaGibhiyoni, Jeyieri; zita romukadzi wake rakanga riri Maaka.",
            "36": "<sup>36</sup> Mwanakomana wake wedangwe waiva Abhidhoni, tevere Zuri, naKishi, naBhaari, naNeri, naNadhabhi;",
            "37": "<sup>37</sup> naGedhori, naAhio, naZekariya, naMikiroti.",
            "38": "<sup>38</sup> Mikiroti wakabereka Shimeami. Ivo vaigarawo pamwechete nehama dzavo paJerusaremu, pakatarisana nehama dzavo.",
            "39": "<sup>39</sup> Neri akabereka Kishi, Kishi akabereka Sauro, Sauro akabereka Jonatani, naMarikishua, naAbhinadhabhu, naEshibhaari.",
            "40": "<sup>40</sup> Mwanakomana waJonatani waiva Meribhibhaari. Meribhibhaari akabereka Mika.",
            "41": "<sup>41</sup> Vanakomana vaMika: Pitoni, naMereki, naTarea, naAhazi.",
            "42": "<sup>42</sup> Ahazi akabereka Jara, Jara akabereka Arimeti naAzimavheti, naZimiri; Zimiri akabereka Moza;",
            "43": "<sup>43</sup> Moza akabereka Bhinea, naRefaya mwanakomana wake, naEreasa mwanakomana wake, naAzeri mwanakomana wake;",
            "44": "<sup>44</sup> Azeri akanga ana vanakomana vatanhatu; mazita avo ndiwo: Azirikami, naBhokeru, naIshimaeri, naShearia, naObhadhiya, naHanani. Ndivo vanakomana vaAzeri."
        },
        "10":
        {
            "1": "<sup>1</sup> Zvino vaFirisitia vakarwa navaIsiraeri, varume vaIsiraeri vakatiza pamberi pavaFirisitia, vakawira pasi vaurawa pagomo reGiribhoa.",
            "2": "<sup>2</sup> VaFirisitia vakateverera Sauro navanakomana vake kwazvo, vaFirisitia vakauraya Jonatani, naAbhinadhabhu, naMarikishua, vanakomana vaSauro.",
            "3": "<sup>3</sup> Sauro akakurirwa kwazvo pakurwa, vapfuri vouta vakamubata; akatambudzika kwazvo nokuda kwavapfuri vouta.",
            "4": "<sup>4</sup> Ipapo Sauro akati kumubati wenhumbi dzake dzokurwa nadzo, \"Vhomora munondo wako, undibaye nawo, kuti ava vasina kudzingiswa varege kusvika vandidadire.\" Asi mubati wenhumbi dzake dzokurwa nadzo wakaramba, nokuti akanga achitya kwazvo. Naizvozvo Sauro akatora munondo wake, akazviwisira pauri.",
            "5": "<sup>5</sup> Zvino mubati wenhumbi dzake dzokurwa nadzo akati achiona kuti Sauro afa, naiye akazviwisirawo pamunondo wake, akafawo.",
            "6": "<sup>6</sup> Naizvozvo Sauro akafa navanakomana vake vatatu; veimba yake yose vakafa pamwechete.",
            "7": "<sup>7</sup> Zvino varume vose valsiraeri, vakanga vagere pamupata uyo, vakati vachiona kuti vatiza, uye kuti Sauro navanakomana vake vafa, ivo vakabva pamaguta avo, vakatiza; vaFirisitia vakasvika vakagaramo.",
            "8": "<sup>8</sup> Zvino fume mangwana vaFirisitia vakati vachiuya kuzosunungura vakanga vaurawa, vakawana Sauro navanakomana vake vatatu vakafa pagomo reGiribhoa.",
            "9": "<sup>9</sup> Vakamusunungura, vakatora musoro wake nenhumbi dzake dzokurwa nadzo, vakatuma nhume kumativi ose enyika yavaFirisitia, kundozivisa vamwari vavo navanhu vavo shoko iro.",
            "10": "<sup>10</sup> Vakaisa nhumbi dzake dzokurwa nadzo muimba yavamwari vavo, vakasungira musoro wake mumba maDhagoni.",
            "11": "<sup>11</sup> Zvino vanhu vose vaJabheshiGiriyadhi vakati vanzwa zvose zvakanga zvaitirwa Sauro navaFirisitia,",
            "12": "<sup>12</sup> mhare dzose dzikasimuka, vakabvisa chitunha chaSauro nezvitunha zvavanakomana vake vakaenda nazvo Jabheshi, vakaviga mapfupa avo pasi pomuoki paJabheshi, vakatsanya mazuva manomwe.",
            "13": "<sup>13</sup> Naizvozvo Sauro akafa nokuda kokudarika kwake kwaakaitira Jehovha, uye nokuda kweshoko raJehovha raasina kuchengeta; uye nokuti akatsvaka mano kusvikiro, kubvunza naro,",
            "14": "<sup>14</sup> akasabvunza Jehovha; naizvozvo wakamuuraya, akaisa ushe kuna Dhavhidhi mwanakomana waJese."
        },
        "11":
        {
            "1": "<sup>1</sup> Zvino vaIsiraeri vose vakaungana kuna Dhavhidhi paHebhuroni, vakati, \"Tarirai, isu tiri mapfupa enyu nenyama yenyu.",
            "2": "<sup>2</sup> Kare, kunyange Sauro akanga ari mambo, ndimi maibuda nokupinda naIsiraeri; Jehovha Mwari wenyu akati kwamuri, `Iwe uchafudza vanhu vangu valsiraeri, nokuva mutungamiriri \"",
            "3": "<sup>3</sup> Naizvozvo vakuru vose vaIsiraeri vakaenda kuna mambo paHebhuroni, Dhavhidhi akaita sungano paHebhuroni pamberi paJehovha; ivo vakazodza Dhavhidhi, kuti ave mambo waIsiraeri, sezvakanga zvarehwa naJehovha nomuromo waSamueri.",
            "4": "<sup>4</sup> Dhavhidhi navaIsiraeri vose vakakwira Jerusaremu (ndiro Jebhusi); zvino vaJebhusi, vanhu venyika iyo, vakanga varipo.",
            "5": "<sup>5</sup> Ipapo vanhu vaigara paJebhusi vakati kuna Dhavhidhi, \"Haungapindi pano.\" Asi Dhavhidhi akakunda nhare yeZiyoni; ndiro guta raDhavhidhi.",
            "6": "<sup>6</sup> Ipapo Dhavhidhi akati, \"Anotanga kubaya vaJebhusi ndiye achava mutungamiriri nomukuru wehondo.\" Zvino Joabhu mwanakomana waZeruya akatanga kukwira, akaitwa mutungamiriri wehondo.",
            "7": "<sup>7</sup> Dhavhidhi akagara panhare; naizvozvo vakaritumidza zita rinonzi guta raDhavhidhi.",
            "8": "<sup>8</sup> Akavaka guta kumativi ose, kubva paMiro kusvikira kumativi ose; Joabhu akagadzira zvimwe zveguta.",
            "9": "<sup>9</sup> Dhavhidhi akaramba achikura, nokuti Jehovha wehondo aiva naye.",
            "10": "<sup>10</sup> Zvino ndivo vakuru vavarume vane simba vaiva naDhavhidhi, vaimusimbisa paushe hwake, pamwechete navaIsiraeri vose, kuti vamugadze ave mambo, sezvakarehwa naJehovha pamusoro palsiraeri.",
            "11": "<sup>11</sup> Ndiko kuwanda kwavarume vane simba vaiva naDhavhidhi: Joshobhiyami mwanakomana wokuHakimoni, mukuru wavana makumi matatu; iye akasimudza pfumo rake akarwa navana mazana matatu, akavauraya nenguva imwe.",
            "12": "<sup>12</sup> Akamutevera akanga ari Eriazari, mwanakomana waDhodho muAhohi, mumwe wavarume vatatu vaiva nesimba.",
            "13": "<sup>13</sup> Iye akanga ana Dhavhidhi paPasidhamini; vaFirisitia vakanga vaunganapo kuzorwa pabindu rakanga rizere nebhari; vanhu vakatiza pamberi pavaFirisitia.",
            "14": "<sup>14</sup> Vakamira pakati pebindu, vakarirwira, vakauraya vaFirisitia, Jehovha akavarwira nokukunda kukuru.",
            "15": "<sup>15</sup> Zvino vatatu vavakuru vavana makumi matatu vakaburukira kudombo kuna Dhavhidhi kubako reAdhuramu; zvino hondo yavaFirisitia yakanga yadzika matende pamupata weRefaimu.",
            "16": "<sup>16</sup> Zvino nenguva iyo Dhavhidhi akanga ari munhare, uye boka ravarwi vavaFirisitia raiva paBheterehemu nenguva iyo.",
            "17": "<sup>17</sup> Dhavhidhi akapanga, akati, \"Haiwa! Dai mumwe achindipa mvura yetsime reBheterehemu, riri pasuo, ndimwe!\"",
            "18": "<sup>18</sup> Ipapo mhare idzo nhatu dzakapasanura napakati pehondo yavaFirisitia, vakandoteka mvura patsime reBheterehemu, riri pasuo, vakaitora, vakauya nayo kuna Dhavhidhi; asi Dhavhidhi wakaramba kuimwa, akaidururira kuna Jehovha,",
            "19": "<sup>19</sup> akati, \"Mwari wangu ngaandidzivise, ndirege kuita chinhu ichi! Ndingamwa ropa ravarume ava vakaenda vasingarangariri upenyu hwavo here? Nokuti vakauya nayo vasingarangariri upenyu hwavo.\" Naizvozvo akaramba kuimwa. Ndizvo zvakaitwa nemhare idzo nhatu.",
            "20": "<sup>20</sup> Abhishai munun'una waJoabhu, ndiye akanga ari mukuru wavatatu avo, nokuti akasimudza pfumo rake akarwa navana mazana matatu, akavauraya; akava nomukurumbira pakati pavatatu avo.",
            "21": "<sup>21</sup> Pakati pavatatu ndiye wakanga ane mbiri kukunda vamwe vaviri, akava mukuru wavo; asi haana kuenzana navatatu vokutanga.",
            "22": "<sup>22</sup> Bhenaya mwanakomana waJehoyadha, mwanakomana wemhare weKabhizeeri, akanga aita mabasa makuru, iye akauraya vanakomana vaviri vaArieri waMoabhi, akandourayawo shumba mukati mehunza panguva yechando.",
            "23": "<sup>23</sup> Akaurayawo muIjipiti, murume akanga akakura kwazvo akasvika makubhiti mashanu; muljipiti uyu akanga akabata pfumo muruoko rwake, asi iye akaburukira kwaari akabata tsvimbo, akabvuta pfumo muruoko rwomuIjipiti, akamuuraya nepfumo rake iro.",
            "24": "<sup>24</sup> Zvinhu izvi zvakaitwa naBhenaya mwanakomana waJehoyadha, akanga akakurumbira pakati pemhare idzo nhatu.",
            "25": "<sup>25</sup> Tarirai, iye akanga ane mbiri kukunda vana makumi matatu, asi haana kuenzana navatatu vokutanga. Dhavhidhi akamuita mutariri wavarindi vake.",
            "26": "<sup>26</sup> Mhare dzehondo dzaiva: Ashaheri munun'una waJoabhu, naErihanani mwanakomana waDhodho weBheterehemu;",
            "27": "<sup>27</sup> naShamoti muHarodhi, naHerezi muPeroni;",
            "28": "<sup>28</sup> naIra mwanakomana wIkeshi muTekoite, naAbhiezeri muAnatoti;",
            "29": "<sup>29</sup> naSibekai muHushati, naIrai muAhohi;",
            "30": "<sup>30</sup> naMakarai muNetofati, naHeredhi mwanakomana waBhaana muNetofati;",
            "31": "<sup>31</sup> naItai mwanakomana waRibhai weGibhiya wavana vaBhenjamini, naBhenaya muPiratoni;",
            "32": "<sup>32</sup> naHurai wehova dzeGaashi, naAbhieri muAribhati;",
            "33": "<sup>33</sup> naAzimavheti muBhaharumiti, naEriabha muShaaribhoni;",
            "34": "<sup>34</sup> navanakomana vaHashemi muGizoni, naJonatani mwanakomana waShage muHarari,",
            "35": "<sup>35</sup> naAhiami mwanakomana waSakari muHarari, naErifari mwanakomana waUri;",
            "36": "<sup>36</sup> naHeferi muMekerati, naAhiya muPeroni;",
            "37": "<sup>37</sup> naHezero muKarimeri, naNaarai mwanakomana waEzibha;",
            "38": "<sup>38</sup> naJoeri munun'una waNatani, naMibhari mwanakomana waHagiri;",
            "39": "<sup>39</sup> naZereki muAmoni, naNaharai muBheroti, mubati wenhumbi dzokurwa nadzo dzaJoabhu mwanakomana waZeruya;",
            "40": "<sup>40</sup> naIra muItiri, naGarebhi muItiri,",
            "41": "<sup>41</sup> naUriya muHiti, naZabhadhi mwanakomana waArai;",
            "42": "<sup>42</sup> naAdhina mwanakomana waShiza muRubheni, mukuru wavaRubheni, navana makumi matatu naye;",
            "43": "<sup>43</sup> naHanani mwanakomana waMaaka, naJoshafati muMitini;",
            "44": "<sup>44</sup> naUziya muAshiterati, naShama naJeyieri vanakomana vaHotami muAroeri;",
            "45": "<sup>45</sup> naJedhiaeri mwanakomana waShimiri, naJoha munun'una wake, muTizi;",
            "46": "<sup>46</sup> naErieri muMahavhi, naJeribhai, naJoshavhia, vanakomana vaErinaami, naItima muMoabhu;",
            "47": "<sup>47</sup> naErieri, naObhedhi, naJaasieri muMezobha."
        },
        "12":
        {
            "1": "<sup>1</sup> Ava ndivo vakaenda kuna Dhavhidhi paZikiragi, achakavanda nokuda kwaSauro mwanakomana waKishi; ivo vaiva mhare, vaimubatsira pakurwa.",
            "2": "<sup>2</sup> Vakanga vakabata uta, uye vaigona kuposha mabwe, nemiseve nouta noruoko rworudyi kana rworuboshwewo; dzaiva hama dzaSauro vokwaBhenjamini.",
            "3": "<sup>3</sup> Mukuru akanga ari Ahiezeri, tevere Joashi, vanakomana vaShemaa muGibhiyati; naJezieri, naPereti, vanakomana vaAzimavheti; naBheraka, naJehu muAnatoti;",
            "4": "<sup>4</sup> naIshimaya muGibhiyoni, mhare pakati pavana makumi matatu, wairaira vana makumi matatu; naJeremiya, naJahazieri, naJohwanani, naJozabhadhi muGedherati;",
            "5": "<sup>5</sup> naEruzai, naJerimoti, naBheriya, naShemaria, naShefatia muHarufi;",
            "6": "<sup>6</sup> naErikana, naIshiya, naAzareri, naJoezeri, naJoshobhiyami, vaKora;",
            "7": "<sup>7</sup> naJoera, naZebhedhia vanakomana vaJehorami muGedhori.",
            "8": "<sup>8</sup> Vamwe vavaGadhi vakazvitsaurawo, vakaenda kuna Dhavhidhi kunhare kurenje, varume vane simba noumhare, varume vakanga vadzidza kurwa, vakanga vachigona kubata nhovo nepfumo; zviso zvavo zvakanga zvakaita sezveshumba, uye vaimhanya semharapara pamakomo;",
            "9": "<sup>9</sup> Ezeri akanga ari mukuru, wechipiri Obhadhiya, wechitatu Eriabhi;",
            "10": "<sup>10</sup> wechina Misimana, weshanu Jeremiya;",
            "11": "<sup>11</sup> wetanhatu Atai, wechinomwe Erieri;",
            "12": "<sup>12</sup> worusere Johwanani, wepfumbamwe Erizabhadhi;",
            "13": "<sup>13</sup> wegumi Jeremiya; wegumi nomumwe Makibhanai.",
            "14": "<sup>14</sup> Ndivo kuvana vaGadhi vaiva vakuru vehondo; muduku kwavari akanga akaenzana navane zana, nomukuru navane chiuru chezana.",
            "15": "<sup>15</sup> Ndivo vakayambuka Jorodhani pamwedzi wokutanga, panguva yarwakafashamira kunhivi dzarwo dzose; vakadzinga vose vakanga vagere pamipata, kumabvazuva nokumavirira.",
            "16": "<sup>16</sup> Navamwe vana vaBhenjamini navaJudha vakaendawo kuna Dhavhidhi munhare.",
            "17": "<sup>17</sup> Dhavhidhi akabuda akasangana navo, akapindura, akati kwavari, \"Kana mauya kwandiri norugare kuzondibatsira, moyo wangu uchava nemi; asi kana mauya kuzondinyengera, kundiisa kuvadzivisi vangu, kunyange ndisine zvakaipa pamaoko angu, Mwari wamadzibaba edu ngaazvione, akutukei pamusoro pazvo.\"",
            "18": "<sup>18</sup> Ipapo mweya wakauya pamusuro paAmasai aiva mukuru wavana makumi matatu, akati, \"Tiri vanhu venyu, Dhavhidhi, tiri kurutivi rwenyu, mwanakomana waJese! Rugare, rugare ngaruve nemi, rugare ngaruve navabatsiri venyu; nokuti Mwari wenyu anokubatsirai.\" Ipapo Dhavhidhi akavagamuchira, akavaita vakuru veboka.",
            "19": "<sup>19</sup> KwaManasewo vakabva vamwe, vakaenda kuna Dhavhidhi, nguva yaakaenda navaFirisitia kundorwa naSauro, asi havana kuvabatsira; nokuti madzishe avaFirisitia akarangana, akamudzosa, achiti, \"Achatinyengera, achienda kunatenzi wake Sauro, misoro yedu ikava panjodzi.\"",
            "20": "<sup>20</sup> Zvino wakati achienda Zikiragi, vamwe vaManase vakabva, vakasvika kwaari, vaiti, Adhana, naJozabhadhi, naJedhiaeri, naMikaeri, naJozabhadhi, naErihu, naZiretai, vakanga vari vakuru vechiuru chamazana pakati paManase.",
            "21": "<sup>21</sup> Vakabatsira Dhavhidhi pakurwa neboka ramakororo; nokuti vose vakanga vari varume vane simba noumhare, navakuru pahondo.",
            "22": "<sup>22</sup> Zuva rimwe nerimwe vakasvika kuna Dhavhidhi kuzomubatsira, kusvikira yava hondo huru, sehondo yaMwari.",
            "23": "<sup>23</sup> Ndiko kuwanda kwavakuru vakashongera kurwa, vakaenda kuna Dhavhidhi paHebhuroni, kuzodzosera ushe hwaSauro kwaari, sezvakarehwa naJehovha.",
            "24": "<sup>24</sup> Vana vaJudha, vakanga vakashonga nhovo nepfumo, vaiva nezviuru zvitanhatu namazana masere, vakanga vakashongera kurwa.",
            "25": "<sup>25</sup> Kuvana vaSimiyoni varume vane simba noumhare pakurwa vaiva nezviuru zvinomwe nezana rimwe.",
            "26": "<sup>26</sup> Kuvana vaRevhi vaiva nezviuru zvina namazana matanhatu.",
            "27": "<sup>27</sup> Jehoyadha aiva mutungamiriri weimba yaAroni; vakanga vanaye vaiva nezviuru zvitatu namazana manomwe;",
            "28": "<sup>28</sup> naZadhoki, jaya raiva nesimba noumhare, naveimba yababa vake, vakuru vana makumi maviri navaviri.",
            "29": "<sup>29</sup> Kuvana vaBhenjamini, hama dzaSauro, zviuru zvitatu; nokuti kusvikira panguva iyo vazhinji vavo vakaramba vakanamatira imba yaSauro.",
            "30": "<sup>30</sup> Kuvana vaEfuraimu zviuru zvina makumi maviri namazana masere, varume vane simba noumhare, vakanga vane mbiri padzimba dzamadzibaba avo.",
            "31": "<sup>31</sup> Kuhafu yorudzi rwaManase, zviuru zvine gumi nezvisere, vakanga vakanyorwa mazita avo, vakasvika kuzoita Dhavhidhi mambo.",
            "32": "<sup>32</sup> Kuvana vaIsakari, varume vaiziva nguva zvakanaka, uye kuti vaIsiraeri vanofanira kuitei, vakuru vavo vaiva mazana maviri; hama dzavo dzose dzairairwa navo.",
            "33": "<sup>33</sup> KuvaZebhuruni, avo vaigona kufamba namapoka, nokugona kurongedza hondo, vakabata nhumbi dzose dzokurwa nadzo, vaiva nezviuru zvina makumi mashanu; vaigona kurwa vasine moyo miviri.",
            "34": "<sup>34</sup> KuvaNafutari vakuru vane chiuru chimwe, uye vamwe vakanga vakabata nhovo nepfumo vaiva navo, zviuru zvina makumi matatu nezvinomwe.",
            "35": "<sup>35</sup> KuvaDhani vaigona kurongedza hondo, zviuru zvina makumi maviri nezvisere namazana matanhatu.",
            "36": "<sup>36</sup> KuvaAsheri, avo vaigona kufamba namapoka, nokugona kurongedza hondo, zviuru zvina makumi mana.",
            "37": "<sup>37</sup> Uye mhiri kwaJorodhani, kuvaRubheni navaGadhi nehafu yorudzi rwaManase, avo vaiva nenhumbi dzose dzokurwa nadzo, zviuru zvina makumi maviri.",
            "38": "<sup>38</sup> Ava vose, varume vehondo, vaigona kurongedza hondo, vakasvika paHebhuroni nemoyo yakarurama kwazvo, kuzoita Dhavhidhi mambo wavaIsiraeri vose; navaIsiraeri vamwe vose vakanga vanomoyo mumwe kuita Dhavhidhi mambo.",
            "39": "<sup>39</sup> Vakagarapo naDhavhidhi mazuva matatu, vakadya nokumwa, nokuti hama dzavo dzakanga dzavagadzirira.",
            "40": "<sup>40</sup> Uye vamwe vakanga vagere pedo navo kusvikira kwalsakari naZebhuruni naNafutari, vakauya nezvokudya zvakatakurwa nembongoro, namakamera, namahesera, nenzombe, zviri zvokudya zvoupfu, namapundu amaonde, anamasumbu amazambiringa akaoma, newaini, namafuta, nenzombe, namakwai mazhinji, nokuti mufaro wakanga uripo pakati palsiraeri."
        },
        "13":
        {
            "1": "<sup>1</sup> Zvino Dhavhidhi akarangana navakuru vechiuru chamazana navamazana, navatungamiriri vose,",
            "2": "<sup>2</sup> Dhavhidhi akati kuungano yose yaIsiraeri, \"Kana imwi muchiti zvakanaka, uye kana zvichibva kuna Jehovha Mwari wedu, ngatitume shoko kose kuhama dzedu dzakasara munyika yose yalsiraeri, nokuvaprista navaRevhi vari mumaguta avo anamafuro, kuti vaungane nesu;",
            "3": "<sup>3</sup> tidzoserezve areka yaMwari wedu kwatiri, nokuti hatina kuirangarira pamazuva aSauro.\"",
            "4": "<sup>4</sup> Ungano yose ikatenda kuzviita; nokuti vanhu vose vakati zvakanaka.",
            "5": "<sup>5</sup> Zvino Dhavhidhi akaunganidza vaIsiraeri vose, kubva paShihori irwo rukova rweIjipiti, kusvikira pavanopinda paHamati, kuti vatore areka yaMwari paKiriatijearimi.",
            "6": "<sup>6</sup> Dhavhidhi akakwira, iye navaIsiraeri vose, vakaenda Bhaara, ndiro Kiriatijearimi raiva raJudha, kuzotorapo areka yaMwari, iye Jehovha agere pamusoro pamakerubhi, pavanonamata Zita rake.",
            "7": "<sup>7</sup> Vakatakura areka yaMwari pangoro itsva, vakaibvisa paimba yaAbhinadhabhu; Uza naAhio vakafambisa ngoro.",
            "8": "<sup>8</sup> Dhavhidhi navaIsiraeri vose vakatamba pamberi paMwari nesimba ravo rose nenziyo, nembira, nemitengerangwa, nengoma, namakandira, nehwamanda.",
            "9": "<sup>9</sup> Zvino vakati vachisvika paburiro raKidhoni, Uza akatambanudza ruoko rwake kuti abate areka, nokuti nzombe dzakanga dzogumburwa.",
            "10": "<sup>10</sup> Kutsamwa kwaJehovha kukamukira Uza, akamuuraya, nokuti akatambanudza ruoko rwake kuti abate areka; akafirapo pamberi paMwari.",
            "11": "<sup>11</sup> Ipapo Dhavhidhi akaora moyo nokuti Jehovha akarova Uza; akatumidza nzvimbo iyo zita rinonzi Pereziuza kusvikira zuva ranhasi.",
            "12": "<sup>12</sup> Dhavhidhi akatya Mwari nomusi uyo, akati, \"Ndingaisa areka yaMwari kumusha kwangu seiko?\"",
            "13": "<sup>13</sup> Naizvozvo Dhavhidhi haana kuisa areka yaMwari kumusha kwaDhavhidhi, asi akaitsausira kumba kwaObhedhi-Edhomu muGiti.",
            "14": "<sup>14</sup> Areka yaMwari ikagara pamhuri yaObhedhi-Edhomu mumba make mwedzi mitatu; Jehovha akaropafadza imba yaObhedhi-Edhomu nezvose zvaakanga anazvo."
        },
        "14":
        {
            "1": "<sup>1</sup> Zvino Hirami mambo weTiro akatuma nhume kuna Dhavhidhi, namatanda emisidhari, navavaki, navavezi, kuti vamuvakire imba.",
            "2": "<sup>2</sup> Dhavhidhi akaona kuti Jehovha akamusimbisa kuti ave mambo waIsiraeri, nokuti ushe hwake hwakanga hwakudzwa kwazvo nokuda kwavanhu vake Isiraeri.",
            "3": "<sup>3</sup> Dhavhidhi akazvitorerazve vamwe vakadzi paJerusaremu, Dhavhidhi akabereka vamwe vanakomana navanasikana.",
            "4": "<sup>4</sup> Zvino ndiwo mazita avana vaakanga anavo paJerusaremu: Shamua, naShobhabhi, naNatani, naSoromoni;",
            "5": "<sup>5</sup> naIbhari, naErishua, naEripereti;",
            "6": "<sup>6</sup> naNoga, naNefegi, naJafia;",
            "7": "<sup>7</sup> naErishama, naBheriyadha, naErifereti.",
            "8": "<sup>8</sup> Zvino vaFirisitia vakati vanzwa kuti Dhavhidhi azodzwa kuti ave mambo wavaIsiraeri vose, vaFirisitia vose vakakwira kundotsvaka Dhavhidhi; Dhavhidhi akazvinzwa, akabuda kuzorwa navo.",
            "9": "<sup>9</sup> Zvino vaFirisitia vakanga vasvika, vakapararira pamupata weRefaimu.",
            "10": "<sup>10</sup> Dhavhidhi akabvunza Jehovha, akati, \"Ndokwira kundorwa navaFirisitia here? Muchavaisa mumaoko angu here?\" Jehovha akati kuna Dhavhidhi, \" Kwira, nokuti ndichavaisa mumaoko ako.\"",
            "11": "<sup>11</sup> Vakasvika paBhaariperazimi, Dhavhidhi akavaparadzapo; Dhavhidhi akati, \"Mwari akapwanya vavengi vangu noruoko rwangu, sokupwanya kwemvura.\" Naizvozvo vakatumidza nzvimbo iyo Bhaariperazimi.",
            "12": "<sup>12</sup> VaFirisitia vakasiya vamwari vavopo, Dhavhidhi akaraira, vakavapisa nomoto.",
            "13": "<sup>13</sup> VaFirisitia vakapararirazve pamupata uyo.",
            "14": "<sup>14</sup> Ipapo Dhavhidhi akabvunza Mwari zve; Mwari akati kwaari, \"Usandovatevera; tsauka ubve kwavari, ugovavamba pakatarisana nemiti yemibharisami.",
            "15": "<sup>15</sup> Zvino kana uchinzwa kutinhira kokufamba pamisoro yemiti yemibharisami, ipapo unofanira kundorwa; nokuti Mwari akutungamirira kundoparadza hondo yavaFirisitia.\"",
            "16": "<sup>16</sup> Dhavhidhi akaita sezvakarairwa naMwari; vakaparadza hondo yavaFirisitia kubva paGibhiyoni kusvikira paGezeri.",
            "17": "<sup>17</sup> Mbiri yaDhavhidhi ikabudira kunyika dzose; Jehovha akavhundusa marudzi ose pamusoro pake."
        },
        "15":
        {
            "1": "<sup>1</sup> Dhavhidhi akazvivakira dzimba muguta raDhavhidhi; akagadzirira areka yaMwari nzvimbo, akaidzikira tende.",
            "2": "<sup>2</sup> Zvino Dhavhidhi akati, \"Hakuna munhu anofanira kutakura areka yaMwari asi vaRevhi; nokuti ndivo vakatsaurwa naJehovha kutakura areka yaMwari, nokumushumira nokusingaperi.\"",
            "3": "<sup>3</sup> Dhavhidhi akaunganidza vaIsiraeri vose paJerusaremu kuti vaise areka yaJehovha kunzvimbo yayo, yaakanga aigadzirira.",
            "4": "<sup>4</sup> Dhavhidhi akaunganidza vanakomana vaAroni navaRevhi;",
            "5": "<sup>5</sup> pakati pavanakomana vaKohati, Urieri mukuru, nehama dzake dzine zana namakumi maviri;",
            "6": "<sup>6</sup> pakati pavanakomana vaMerari: Asaya mukuru, nehama dzake dzina mazana maviri namakumi maviri;",
            "7": "<sup>7</sup> pakati pavanakomana vaGerishomi: Joeri mukuru, nehama dzake dzine zana namakumi matatu.",
            "8": "<sup>8</sup> Pakati pavanakomana vaErizafani: Shemaya mukuru, nehama dzake dzina mazana maviri;",
            "9": "<sup>9</sup> pakati pavanakomana vaHebhuroni: Erieri mukuru, nehama dzake dzina makumi masere;",
            "10": "<sup>10</sup> pakati pavanakomana vaUzieri. Aminadhabhu mukuru, nehama dzake dzine zana negumi navaviri.",
            "11": "<sup>11</sup> Dhavhidhi akadana Zadhoki naAbhiyatari vaprista, navaRevhi vaiti Urieri, naAsaya, naJoeri, naShemaya, naErieri, naAminadhabhu.",
            "12": "<sup>12</sup> Akati kwavari, \"Ndimi vakuru vedzimba dzamadzibaba avaRevhi; chizvinatsai imwi nehama dzenyu, kuti mukwire neareka yaJehovha Mwari waIsiraeri, kunzvimbo yandakaigadzirira.",
            "13": "<sup>13</sup> \"Nokuti nemhaka yokuti hamuna kuitakura pakutanga, Jehovha Mwari wedu akatirova, nokuti hatina kumutsvaka sezvatakarairwa.\"",
            "14": "<sup>14</sup> Naizvozvo vaprista navaRevhi vakazvinatsa, kuti vakwire neareka yaJehovha Mwari waIsiraeri.",
            "15": "<sup>15</sup> Vana vaRevhi vakatakura areka yaMwari pamafudzi avo namatanda akanga aripo, sezvakarairwa naMozisi neshoko raJehovha.",
            "16": "<sup>16</sup> Dhavhidhi akataura navakuru vavaRevhi kuti vagadze hama dzavo vave vaimbi vane zvokuridza nazvo, zvaiti mitengeramwa, nembira, namakandira, vazviridze nesimba nokuimbisa nomufaro.",
            "17": "<sup>17</sup> Ipapo vaRevhi vakagadza Hemani mwanakomana waJoeri, napakati pehama dzake, Asafi mwanakomana waBherekia; napakati pavanakomana vaMerari, hama dzavo, Etani mwanakomana waKushaya;",
            "18": "<sup>18</sup> uye pamwechete navo hama dzavo dzeboka rechipiri, Zekariya, naBheni, naJaazieri, naShemiramoti, naJehieri, naUni, naEriabhi, naBhenaya, naMaaseya, naMatitia, naEriferehu, naMikineya, naObhedhi-Edhomu, naJeyieri, vatariri vemikova.",
            "19": "<sup>19</sup> Saizvozvovo vaimbi vaiti Hemani, naAsafi, naEtani, vakagadzwa, vanamakandira endarira kuti vaaridze nesimba;",
            "20": "<sup>20</sup> naZekariya, naAzieri, naShemiramoti, naJehieri, naUni, naEriabhi, naMaaseya, naBhenaya, vane mitengerangwa ina manzwi matete;",
            "21": "<sup>21</sup> naMatitia, naEriferehu, naMikineya, naObhedhi-Edhomu, naJeyieri, naAzaziya, vane mbira dzina manzwi makobvu kutungamirira vamwe.",
            "22": "<sup>22</sup> Kenania mukuru wavaRevhi, ndiye waiimbisa vanhu; ndiye waidzidzisa vanhu kuimba, nokuti akanga achiziva izvozvo.",
            "23": "<sup>23</sup> Bherekia naErikana vakanga vari vatariri vemikova yeareka.",
            "24": "<sup>24</sup> Shebhanaya, naJoshafati, naNetaneri, naAmasai, naZekariya, naBhenaya, naEriezeri, vaprista, ndivo vairidza hwamanda pamberi peareka yaMwari; Obhedhi-Edhomu naJehiya vakanga vari vatariri vemikova yeAreka.",
            "25": "<sup>25</sup> Zvino Dhavhidhi navakuru vaIsiraeri, navakuru vechiuru chamazana, vakandobvisa areka yesungano yaJehovha mumba maObhedhi-Edhomu nomufaro.",
            "26": "<sup>26</sup> Zvino Mwari akati achibatsira vaRevhi vakanga vachitakura areka yesungano yaJehovha, vakabayira Mwari nzombe nomwe namakondobwe manomwe.",
            "27": "<sup>27</sup> Dhavhidhi akanga akafuka nguvo yomucheka wakanaka, navaRevhi vose vakanga vachitakura areka, navaimbi, naKenania, mudzidzisi wokuimba, pamwechete navaimbi; uye Dhavhidhi akanga akafuka efodhi yomucheka.",
            "28": "<sup>28</sup> Saizvozvo vaIsiraeri vose vakakwira neareka yesungano yaJehovha vachipururudza, nokuridza mimanzi, nehwamanda, namakandira, vachiridza zvikuru mitengeramwa nembira.",
            "29": "<sup>29</sup> Zvino areka yesungano yaJehovha yakati ichisvika muguta raDhavhidhi, Mikaeri mukunda waSauro, akatarira napahwindo, akaona mambo Dhavhidhi achipembera nokutamba; akamushora mumoyo make."
        },
        "16":
        {
            "1": "<sup>1</sup> Vakapinza areka yaMwari, vakaiisa pakati petende rayakanga yadzikirwa naDhavhidhi; vakabayira pamberi paMwari zvipiriso zvinopiswa nezvipiriso zvokuyananisa.",
            "2": "<sup>2</sup> Zvino Dhavhidhi akati apedza kubayira zvipiriso zvinopiswa nezvipiriso zvokuyananisa, akaropafadza vanhu nezita raJehovha.",
            "3": "<sup>3</sup> Akapa mumwe nomumwe wavaIsiraeri, murume nomukadzi, mumwe nomumwe bundu rechingwa, nenhindi yenyama, nebundu ramazambiringa.",
            "4": "<sup>4</sup> Akagadza vamwe vaRevhi, kuti vashumire pamberi peareka yaJehovha, nokurumbidza, nokuvonga, nokukudza Jehovha Mwari waIsiraeri;",
            "5": "<sup>5</sup> mukuru waiva Asafi, tevere Zekariya, naJeyieri, naShemiramoti, naJehieri, naMatitia, naEriabhi, naBhenaya, naObhedhi-Edhomu, naJeyieri, vane mitengeremwa nembira; Asafi airidza kwazvo makandira.",
            "6": "<sup>6</sup> Bhenaya naJahazieri ndivo vairidza hwamanda nguva dzose pamberi peareka yesungano yaMwari.",
            "7": "<sup>7</sup> Zvino nezuva iro Dhavhidhi akatanga kuraira kuti vavonge Jehovha nomuromo waAsafi nehama dzake:",
            "8": "<sup>8</sup> Vongai Jehovha, mudane zita rake; Zivisai zvaakaita pakati pendudzi dzavanhu.",
            "9": "<sup>9</sup> Muimbirei, mumuimbire nziyo dzokukudza; Rondedzerai mabasa ake ose anoshamisa.",
            "10": "<sup>10</sup> Zvirumbidzei muzita rake dzvene; moyo yavanotsvaka Jehovha ngaifare kwazvo.",
            "11": "<sup>11</sup> Tsvakai Jehovha nesimba rake; Tsvakai chiso chake nguva dzose.",
            "12": "<sup>12</sup> Rangarirai mabasa ake anoshamisa, aakaita; Zvishamiso zvake, nezvaakatonga nomuromo wake.",
            "13": "<sup>13</sup> imwi vana vaIsiraeri, muranda wake, imwi vana vaJakobho vasanangurwa vake.",
            "14": "<sup>14</sup> Ndiye Jehovha Mwari wedu, Zvaakatonga zviri pasi pose.",
            "15": "<sup>15</sup> Rangarirai sungano yake nokusingaperi, Iro shoko raakaraira kumarudzi ane chiuru;",
            "16": "<sup>16</sup> Iyo sungano yaakaita naAbhurahamu, Nemhiko yake kuna Isaka;",
            "17": "<sup>17</sup> Akazvisimbisira Jakobho, kuti ave mutemo, Kuna Isiraeri kuti ive sungano isingaperi;",
            "18": "<sup>18</sup> Achiti, \"Ndichakupa iwe nyika yeKenani, uve mugove wenhaka yenyu;\"",
            "19": "<sup>19</sup> Panguva imwi muchiri varume vashoma pakuverengwa, Zvirokwazvo vashoma kwazvo, navatorwa mairi.",
            "20": "<sup>20</sup> Vakafamba-famba vachibva kuno rumwe rudzi vachienda kuno rumwe, Nokubva kunoumwe ushe vachienda kunavamwe vanhu.",
            "21": "<sup>21</sup> Haana kutendera munhu kuvaitira zvakaipa; Zvirokwazvo wakatuka madzimambo nokuda kwavo;",
            "22": "<sup>22</sup> Achiti, \"Musagunzva vazodzwa vangu, Navaporofita vangu musavaitira chakaipa.\"",
            "23": "<sup>23</sup> Imbirai Jehovha, pasi pose; Paridzirai kuponesa kwake zuva rimwe nerimwe,",
            "24": "<sup>24</sup> dudzirai kubwinya. kwake. akati pavahedheni, Namabasa ake anoshamisa pakati pendudzi dzose.",
            "25": "<sup>25</sup> Nokuti Jehovha mukuru, anofanira kurumbidzwa kwazvo; Iye anofanira kutyiwa kupfuura vamwari vose.",
            "26": "<sup>26</sup> Nokuti vamwari vose vendudzi zvifananidzo hazvo; Asi Jehovha ndiye akaita kudenga-denga.",
            "27": "<sup>27</sup> Kukudzwa noumambo zviri pamberi pake; Simba nomufaro zviri panzvimbo yake;",
            "28": "<sup>28</sup> Ipai Jehovha, imwi ndudzi dzavanhu, Ipai Jehovha kukudzwa nesimba.",
            "29": "<sup>29</sup> Ipai Jehovha kukudzwa kunofanira zita rake; Uyai nechipiriso, uyai pamberi pake; Namatai kuna Jehovha mune nguvo tsvene.",
            "30": "<sup>30</sup> Dedera pamberi pake, nyika yose Nyikawo yakasimbiswa kuti irege kuzununguswa.",
            "31": "<sup>31</sup> Kudenga-denga ngakufare, napasi ngapafarisise; Pakati pendudzi ngavati, \"Jehovha anobata ushe.\"",
            "32": "<sup>32</sup> Gungwa ngaritinhire, nokuzara kwaro; Sango ngarifarisise kwazvo, nezvose zviri mariri;",
            "33": "<sup>33</sup> Nguva iyo miti yedondo ichaimba nomufaro pamberi paJehovha; Nokuti anouya kuzotonga pasi.",
            "34": "<sup>34</sup> Vongai Jehovha; nokuti akanaka; Nokuti nyasha dzake dzinogara nokusingaperi.",
            "35": "<sup>35</sup> Muti, \"Tiponesei, Mwari woruponeso rwedu, Mutiunganidze pamwechete, mutirwire pandudzi, Kuti tivonge zita renyu dzvene, Tifarisise pakukurumbidzai.\"",
            "36": "<sup>36</sup> Jehovha Mwari walsiraeri ngaakudzwe, Kubva pakusingaperi kusvikira pakusingaperi. Vanhu vose vakati, \"Ameni,\" vakarumbidza Jehovha.",
            "37": "<sup>37</sup> Zvino akasiyapo pamberi peareka yesungano yaJ ehovha Asafi nehama dzake, kuti vashumire pamberi peareka nguva dzose, sezvakafanira zuva rimwe nerimwe;",
            "38": "<sup>38</sup> naObhedhi-Edhomu nehama dzavo, makumi matanhatu navasere; naObhedhi-Edhomuwo mwanakomana waJedhutuni, naHosa, kuti vave vatariri vemikova;",
            "39": "<sup>39</sup> nomuprista Zadhoki, nehama dzake vaprista, pamberi petabhenakeri yaJehovha padunhu rakakwirira rakanga riri paGibhiyoni;",
            "40": "<sup>40</sup> kuti vabayire Jehovha zvipiriso zvinopiswa, nguva dzose, mangwanani namadeko, paaritari yezvipiriso zvinopiswa, sezvakanyorwa zvose mumurayiro waJehovha, waakaraira Isiraeri;",
            "41": "<sup>41</sup> uye pamwechete navo Hemani naJedhutuni navamwe vakatsaurwa, vakanga varehwa mazita avo, kuti vavonge Jehovha; nokuti nyasha dzake dzinogara nokusingaperi;",
            "42": "<sup>42</sup> uye pamwechete navo Hemani naJedhutuni vane hwamanda namakandira, zvaifanira kuridzwa navaridzi, uye zvokuridzira nziyo dzaMwari; uye vanakomana vaJedhutuni kuti vatarire suo.",
            "43": "<sup>43</sup> Vanhu vose vakaenda mumwe nomumwe kumba kwake; Dhavhidhi akadzoka kuzoropafadza imba yake."
        },
        "17":
        {
            "1": "<sup>1</sup> Zvino Dhavhidhi akati agere mumba make, Dhavhidhi akati kumuporofita Natani, \"Tarira, ini ndigere muimba yomusidhari, asi areka yesungano yaJehovha igere pasi petende.\"",
            "2": "<sup>2</sup> Natani akati kuna Dhavhidhi, \"Itai henyu zvose zviri mumoyo menyu, nokuti Mwari anemi.\"",
            "3": "<sup>3</sup> Zvino nousiku uhwo shoko raMwari rakasvika kuna Natani, richiti,",
            "4": "<sup>4</sup> \"Enda unoudza Dhavhidhi muranda wangu, uti, `Iwe haungandivakiri imba mandingagara,",
            "5": "<sup>5</sup> nokuti handina kugara mumba kubva pazuva randakandotora vaIsiraeri, kusvikira zuva ranhasi; asi ndakabva patende rimwe ndichienda kune rimwe, napatabhenakeri imwe ndichienda kune imwe.",
            "6": "<sup>6</sup> Pose pandakafamba navaIsiraeri vose, ndakatongotaura here shoko rimwe kune mumwe wavatongi vaIsiraeri vose, vandakaraira kufudza vanhu vangu,' ndichiti `Makaregereiko kundivakira imba yomusidhari?' \"",
            "7": "<sup>7</sup> Naizvozvo zvino, unofanira kuti kumuranda wangu Dhavhidhi, \"Zvanzi naJehovha wehondo, `Ndakakutora kumafuro, kwawakanga uchifudza makwai, kuti uve mubati wavanhu vangu vaIsiraeri;",
            "8": "<sup>8</sup> ndikava newe pose pawakafamba, ndikaparadza vavengi vako vose pamberi pako; ndichakuitira zita ringafanana namazita avakuru vari panyika.",
            "9": "<sup>9</sup> Ndichagadzirira vanhu vangu valsiraeri nzvimbo, ndichavasima, vagare panzvimbo yavo, varege kubviswazve; navakaipa havangavaparadzizve sapakutanga,",
            "10": "<sup>10</sup> nokubva pazuva randakaraira vatongi kuti vabate vanhu vangu valsiraeri; uye ndichakunda vavengi vako vose. Uye ndinokuudza kuti Jehovha achakumisira imba.'",
            "11": "<sup>11</sup> \"Zvino kana nguva yako yasvika, yaunofanira kuenda nayo kunamadzibaba ako, ndichamutsa mumwe worudzi rwako ruchakutevera, uchava mumwe wavanakomana vako; ndichasimbisa ushe hwake.",
            "12": "<sup>12</sup> \"Iye achandivakira imba, neni ndichasimbisa chigaro chake choushe nokusingaperi.",
            "13": "<sup>13</sup> Ini ndichava baba vake, naiye achava mwanakomana wangu; handingabvisi nyasha dzangu kwaari, sezvandakadzibvisa kuna iye wakakutangira;",
            "14": "<sup>14</sup> asi ndichamugarisa mumba mangu nomuushe hwangu nokusingaperi; chigaro chake choushe chichasimbiswa nokusingaperi.\"",
            "15": "<sup>15</sup> Natani akaudza Dhavhidhi mashoko awa ose, nezvose zvaakaratidzwa.",
            "16": "<sup>16</sup> Ipapo mambo Dhavhidhi akapinda, akagara pamberi paJehovha, akati, \"Jehovha Mwari, ndini aniko, neimba yangu chinyiko, zvamakandisvitsa kusvikira pano?",
            "17": "<sup>17</sup> \"Asi chinhu ichi chakanga chiri chiduku pamberi penyu, Mwari, asi makataura pamusoro peimba yomuranda wenyu kusvikira panguva huru inozouya, mukandiitira sezvinoitirwa munhu anokudzwa kwazvo, Jehovha Mwari.",
            "18": "<sup>18</sup> Dhavhidhi angareveikozve kwamuri pamusoro pokukudzwa komuranda wenyu? Nokuti munoziva muranda wenyu.",
            "19": "<sup>19</sup> Jehovha makaita izvi zvikuru zvose nokuda komuranda wenyu, uye nokuda komoyo wenyu, kuti muzivise zvinhu izvi zvose zvikuru.",
            "20": "<sup>20</sup> Jehovha, hakuna mumwe akafanana nemi, hakuna mumwe Mwari asi imwi moga, sezvatakanzwa zvose nenzeve dzedu.",
            "21": "<sup>21</sup> Rumwe rudzi ruripiko panyika rwakafanana navanhu venyu vaIsiraeri, vakandodzikunurwa naMwari, vave vanhu vake, kuti imwi mukurumbire nezvinhu zvikuru nezvinotyisa, zvamakadzinga ndudzi pamberi pavanhu venyu, vamakadzikunura paljipiti?",
            "22": "<sup>22</sup> Nokuti vanhu venyu vaIsiraeri makavaita vanhu venyu pauzima nokusingaperi; imwi Jehovha mukava Mwari wavo.",
            "23": "<sup>23</sup> \"Zvino Jehovha shoko ramakataura pamusoro pomuranda wenyu napamusoro peimba yake ngarisimbiswe nokusingaperi, muite sezvamakataura.",
            "24": "<sup>24</sup> Uye zita renyu ngarisimbiswe nokukudzwa nokusingaperi, zvichinzi, `Jehovha wehondo ndiye Mwari waIsiraeri; ndiye Mwari kuna Isiraeri;' imba yaDhavhidhi muranda wenyu ichamiswa pamberi penyu.",
            "25": "<sup>25</sup> Nokuti imi, Mwari wangu, makazivisa muranda wenyu kuti muchamumisira imba; saka muranda wenyu akatsunga kunyengetera pamberi penyu.",
            "26": "<sup>26</sup> Zvino Jehovha, ndimi Mwari, makapikira muranda wenyu chinhu ichi;",
            "27": "<sup>27</sup> zvino makafara nazvo kuropafadza imba yomuranda wenyu, kuti irambe iripo pamberi penyu nokusingaperi; nokuti imwi Jehovha makairopafadza zvino icharopafadzwa nokusingaperi.\""
        },
        "18":
        {
            "1": "<sup>1</sup> Zvino shure kwaizvozvo Dhavhidhi akarwa navaFirisitia akavakunda; akatorera vaFirisitia Gati nemisha yaro.",
            "2": "<sup>2</sup> Akakunda Moabhiwo, vaMoabhi vakaitwa varanda vaDhavhidhi, vakamuvigira zvipo.",
            "3": "<sup>3</sup> Dhavhidhi akakundawo Hadharezeri mambo weZobha, kusvikira paHamati, pakuenda kwake kundosimbisa vumambo bwake parwizi Yufuratesi.",
            "4": "<sup>4</sup> Dhavhidhi akamutorera ngoro dzine chiuru chimwe navatasvi vamabhiza vane zviuru zvinomwe, navaifamba namakumbo vane zviuru zvina makumi maviri; Dhavhidhi akagura marunda amakumbo amabhiza ose aikweva ngoro, asi wakachengetera ngoro dzine zana mamwe awo.",
            "5": "<sup>5</sup> Zvino vaSiria veDhamasiko vakati vachiuya kuzobatsira Hadharezeri mambo weZobha, Dhavhidhi akauraya vaSiria vane zviuru zvina makumi maviri nezviviri.",
            "6": "<sup>6</sup> Dhavhidhi akaisa mapoka avarwi muSiria paDhamasiko; vaSiria vakava varanda vaDhavhidhi vakamuvigira zvipo. Jehovha akakundisa Dhavhidhi pose paakaenda.",
            "7": "<sup>7</sup> Dhavhidhi akatora nhovo dzendarama dzakanga dzakashongwa navaranda vaHadharezeri, akadziisa Jerusaremu.",
            "8": "<sup>8</sup> NapaTibhati napaKuni, maguta eHadharezeri, Dhavhidhi akatora ndarira zhinji kwazvo, Soromoni yaakazoita nayo dziva rendarira, nembiru, nemidziyo yendarira.",
            "9": "<sup>9</sup> Zvino Tou mambo weHamati akati achinzwa kuti Dhavhidhi akaparadza hondo yose yaHadharezeri mambo weZobha,",
            "10": "<sup>10</sup> akatuma Hadhorami mwanakomana wake kuna mambo Dhavhidhi, kundomukwazisa, nokumukorokotedza pamusoro pokurwa kwake kwaHadharezeri nokumukunda; nokuti Hadharezeri wairwa naTou; akanga ane midziyo yamarudzi ose, yendarama, neyesirivha, nendarira.",
            "11": "<sup>11</sup> Neyiyiwo mambo Dhavhidhi akaitsaurira Jehovha, pamwechete nesirivha nendarama yaakanga atora kundudzi dzose; kuna vaEdhomu, navaMoabhi, navana vaAmoni, navaFirisitia, navaAmareki.",
            "12": "<sup>12</sup> Uye Abhishai mwanakomana waZeruya, akauraya vaEdhomu paMupata woMunyu vaiva vane zviuru zvine gumi nezvisere.",
            "13": "<sup>13</sup> Akaisa mapoka avarwi paEdhomu; vaEdhomu vose vakava varanda vaDhavhidhi. Jehovha akakundisa Dhavhidhi pose paakaenda.",
            "14": "<sup>14</sup> Dhavhidhi akabata vaIsiraeri vose; akatongera vanhu vake vose akavaitira zvakarurama.",
            "15": "<sup>15</sup> Joabhu mwanakomana waZeruya ndiye wairaira hondo, naJehoshafati mwanakomana waAhirudhi waiva gurukota,",
            "16": "<sup>16</sup> Zadhoki mwanakomana waAhitubhi, naAhimereki mwanakomana waAbhiyatari, vaiva vaprista, naShavhisha waiva munyori;",
            "17": "<sup>17</sup> uye Bhenaya mwanakomana waJehoyadha ndiye wairaira vaKereti navaPereti; uye vanakomana vaDhavhidhi vaiva vakuru kutevera mambo."
        },
        "19":
        {
            "1": "<sup>1</sup> Shure kwaizvozvo mambo wavana vaAmoni akafa, mwanakomana wake akabata ushe panzvimbo yake.",
            "2": "<sup>2</sup> Zvino Dhavhidhi akati, \"Ndichaitira Hanuni mwanakomana waNahashi zvakanaka, sezvandakaitirwa zvakanaka nababa vake.\" Naizvozvo Dhavhidhi akatuma nhume kumubata ruoko pamusoro pababa vake. Varanda vaDhavhidhi vakasvika panyika yavana vaAmoni kuna Hanuni, kuzomubata ruoko.",
            "3": "<sup>3</sup> Asi machinda avana vaAmoni akati kuna Hanuni ishe wavo, \"Munofunga kuti Dhavhidhi anoda kukudza baba venyu, zvaatuma vabati voruoko kwamuri here? Varanda vake havana kuuya kwamuri kunzvera, nokuputsa, nokushora nyika here?\"",
            "4": "<sup>4</sup> Naizvozvo Hanuni akabata varanda vaDhavhidhi, akaveura ndebvu dzavo, akagura nguvo dzavo napakati kusvikira pamagaro avo, akavadzinga.",
            "5": "<sup>5</sup> Zvino vamwe vakandoudza Dhavhidhi zvakanga zvaitirwa varume. Akatuma vanhu kusangana navo, nokuti varume vakanga vachinyara kwazvo. Mambo akati, \"Garai henyu Jeriko kusvikira ndebvu dzenyu dzamerazve, mugodzoka henyu.\"",
            "6": "<sup>6</sup> Zvino vana vaAmoni vakati vachiona kuti vazvivengesa naDhavhidhi, Hanuni navana vaAmoni vakatuma matarenda esirivha ane chiuru chimwe kuzoripa ngoro navatasvi vamabhiza vanobva Mesopotamia, neArimimaaka, neZobha.",
            "7": "<sup>7</sup> Naizvozvo vakaripira ngoro dzine zviuru zvina makumi matatu nezviviri, namambo weMaaka navanhu vake; ivo vakasvika vakadzika matende avo pamberi peMedhebha. Vana vaAmoni vakaungana vachibva kumaguta avo, vakauya kuzorwa.",
            "8": "<sup>8</sup> Zvino Dhavhidhi akati achizvinzwa, akatuma Joabhu nehondo yose yavarume vane simba.",
            "9": "<sup>9</sup> Vana vaAmoni vakabuda, vakagadzira hondo pavanopinda napo pasuo; asi madzimambo, akanga auya, aiva pachawo oga pasango.",
            "10": "<sup>10</sup> Zvino Joabhu akati achiona kuti wovavarirwa mberi neshure, akatsaura vamwe kunavakatsaurwa vose vaIsiraeri, akavagadzira kuti varwe navaSiria.",
            "11": "<sup>11</sup> Vamwe vanhu, vakasara, akavaisa kuna Abhishai, munun'una wake, vakazvigadzira kuti varwe navana vaAmoni.",
            "12": "<sup>12</sup> Akati, \"Kana vaSiria vakandikurira ini, iwe unofanira kundibatsira; asi kana vana vaAmoni vakakukurira iwe, ini ndichauya kukubatsira.",
            "13": "<sup>13</sup> \"Tsunga moyo, tirwire vanhu vedu namaguta aMwari wedu savarume; naJehovha ngaaite sezvaanoda.\"",
            "14": "<sup>14</sup> Naizvozvo Joabhu navanhu vaakanga anavo vakaswedera kuzorwa navaSiria, ivo vakatiza pamberi pake.",
            "15": "<sup>15</sup> Zvino vana vaAmoni vakati vachionawo kuti vaSiria vatiza, naivo vakatizawo saizvozvo pamberi paAbhishai, vakapinda muguta. Ipapo Joabhu akaenda Jerusaremu.",
            "16": "<sup>16</sup> Zvino vaSiria vakati vachiona kuti vakundwa navaIsiraeri, vakatuma nhume, vakadana vaSiria vakanga vari mhiri korwizi, vakatungamirirwa naShofaki mukuru wehondo yaHadharezeri.",
            "17": "<sup>17</sup> Dhavhidhi akaudzwa izvozvo; akaunganidza valsiraeri vose, vakayambuka Jorodhani, vakasvika kwavari, akazvigadzira kuzorwa navo. Zvino Dhavhidhi akati azvigadzira kuzorwa navaSiria, ivo vakarwa naye.",
            "18": "<sup>18</sup> VaSiria vakatiza pamberi pavaIsiraeri; Dhavhidhi akauraya pakati pavaSiria varume vengoro dzine zviuru zvinomwe, navaifamba namakumbo vane zviuru zvina makumi mana, akaurayawo Shofaki mukuru wehondo.",
            "19": "<sup>19</sup> Zvino varanda vaHadharezeri vakati vachiona kuti vakundwa naIsiraeri vakayanana naDhavhidhi, vakamushumira; vaSiria vakazoramba kubatsira vana vaAmoni."
        },
        "20":
        {
            "1": "<sup>1</sup> Zvino gore rakati rapera, panguva yokurwa kwamadzimambo, Joabhu akaenda nehondo ine simba, akaparadza nyika yavana vaAmoni, akandokomba Rabha. Asi Dhavhidhi akasara Jerusaremu. Joabhu akakunda Rabha, akariputsa.",
            "2": "<sup>2</sup> Dhavhidhi akabvisa korona yamambo wavo pamusoro wake, akawana kuti inosvika tarenda rimwe rendarama pakurema kwayo, uye yakanga ina mabwe anokosha ikaiswa pamusoro waDhavhidhi; akapamba zvizhinji kwazvo paguta iro.",
            "3": "<sup>3</sup> Akabudisawo vanhu vakanga varimo, akavacheka namajeko, nembambo dzamatare, uye namasanhu. Dhavhidhi akaita saizvozvo namaguta ose avana vaAmoni. Dhavhidhi navanhu vose vakadzokera Jerusaremu.",
            "4": "<sup>4</sup> Zvino shure kwaizvozvo kurwa kukazomuka paGezeri navaFirisitia; Shibhekai muHushati, akauraya Sipai, mumwe wavanakomana vaRafa.",
            "5": "<sup>5</sup> Vakarwazve navaFirisitia; Erihanani mwanakomana waJairi akauraya Rami munun'una waGoriati muGiti, rwiriko rwepfumo rake rwakanga rwakafanana nedanda romuruki.",
            "6": "<sup>6</sup> Vakarwazve paGati, pakanga pano munhu murefu kwazvo, akanga ane mimwe nezvigumwe zvose zvina makumi maviri nezvina, mitanhatu paruoko rumwe norumwe, nezvitanhatu parutsoka rumwe norumwe; naiyewo akanga akaberekerwa Rafa.",
            "7": "<sup>7</sup> Zvino akati achishora vaIsiraeri, Jonatani mwanakomana waShimea, munun'una waDhavhidhi akamuuraya.",
            "8": "<sup>8</sup> Ndivo vakaberekerwa Rafa paGati; vakaurawa naDhavhidhi, uye navaranda vake."
        },
        "21":
        {
            "1": "<sup>1</sup> Zvino Satani akamukira vaIsiraeri, akakurudzira Dhavhidhi kuti averenge vaIsiraeri.",
            "2": "<sup>2</sup> Dhavhidhi akati kuna Joabhu nokumachinda avanhu, \"Endai mundoverenga vaIsiraeri kubva paBheerishebha kusvikira paDhani; mudzoke neshoko, ndizive kuwanda kwavo.\"",
            "3": "<sup>3</sup> Joabhu akati, \"Jehovha ngaawedzere vanhu vake runezana pakuwanda kwavo zvino; asi ishe wangu mambo, vose havazi varanda vashe wangu here? Ishe wangu anotsvakireiko chinhu ichi? Achapinzireiko vaIsiraeri pamhosva?\"",
            "4": "<sup>4</sup> Kunyange zvakadaro shoko ramambo rakakurira Joabhu. Zvino Joabhu akaenda, akapota pakati pavaIsiraeri vose, akasvika Jerusaremu.",
            "5": "<sup>5</sup> Joabhu akarondedzera kuwanda kwavanhu kuna Dhavhidhi. Valsiraeri vose vakasvika zviuru zvinezviuru nezviuru zvinezana zvavanhu vaigona kurwa nomunondo; uye vaJudha vakasvika zviuru zvamazana mana namakumi manomwe vaigona kurwa nomunondo.",
            "6": "<sup>6</sup> Asi haana kuverenga vaRevhi navaBhenjamini pakati pavo; nokuti shoko ramambo rakanga rakaipa kwazvo kuna Joabhu.",
            "7": "<sup>7</sup> Zvino Mwari akanga asingafari nechinhu ichi; saka akarova Isiraeri.",
            "8": "<sup>8</sup> Dhavhidhi akati kuna Mwari, \"Ndatadza kwazvo zvandaita chinhu ichi; asi zvino bvisai henyu kuipa komuranda wenyu; nokuti ndaita sebenzi kwazvo.\"",
            "9": "<sup>9</sup> Jehovha akataura naGadhi, muoni waDhavhidhi, akati,",
            "10": "<sup>10</sup> \"Enda unotaura naDhavhidhi, uti, zvanzi naJehovha, `Ndinokuisira zvinhu zvitatu, chizvitsaurira chimwe chazvo, ndikuitire icho.' \"",
            "11": "<sup>11</sup> Zvino Gadhi akaenda kuna Dhavhidhi, akati kwaari, \"Zvanzi naJehovha, `Chitsaura chaunoda;",
            "12": "<sup>12</sup> makore matatu enzara, kana kuparadzwa navavengi vako mwedzi mitatu, uchidzingwa nomunondo wavavengi vako, kana munondo waJehovha mazuva matatu, ndiro denda panyika, mutumwa waJehovha achiparadza panyika yose yaIsiraeri.' Zvino chifunga hako, urangarire mhinduro yandingadzosera kuna iye akandituma.\"",
            "13": "<sup>13</sup> Ipapo Dhavhidhi akati kuna Gadhi, \"Ndiri pakumanikidzwa kukuru; ndiwire hangu zvino muruoko rwaJehovha, nokuti nyasha dzake ihuru, asi ndirege hangu kuwira muruoko rwomunhu.\"",
            "14": "<sup>14</sup> Ipapo Jehovha akatuma denda pakati paIsiraeri; varume vane zviuru zvina makumi manomwe vakafa pakati paIsiraeri.",
            "15": "<sup>15</sup> Mwari akatuma mutumwa Jerusaremu kuzoriparadza; zvino wakati oda kuparadza, Jehovha akatarira, akazvidemba pamusoro pechinhu icho chakaipa, akati kumutumwa akanga achiparadza, \"Zvino, chidzosa ruoko rwako.\" Zvino mutumwa waJehovha akanga amire paburiro raOrinani muJebhusi.",
            "16": "<sup>16</sup> Dhavhidhi akatarira, akaona mutumwa waJehovha amire pakati penyika nokudenga, akabata munondo wakavhomorwa muruoko rwake, wakatambanudzwa pamusoro peJerusaremu. Ipapo Dhavhidhi navakuru vakawira pasi nezviso zvavo, vakafuka saga.",
            "17": "<sup>17</sup> Zvino Dhavhidhi akati kuna Mwari, \"Ko handizi ini here ndakaraira kuti vanhu vaverengwe? Ndini ndakatadza hangu ndikaita zvakaipa zvikuru; asi makwai awa akaiteiko? Jehovha Mwari wangu, ruoko rwenyu ngarundirove harwo ini neimba yababa vangu; asi ngarurege kurova vanhu venyu, nokuvatambudza.\"",
            "18": "<sup>18</sup> Ipapo mutumwa waJehovha akaraira Gadhi kuti audze Dhavhidhi kuti Dhavhidhi akwire avakire Jehovha aritari paburiro raOrinani muJebhusi.",
            "19": "<sup>19</sup> Dhavhidhi akakwira nokureva kwaGadhi kwaakataura nezita raJehovha.",
            "20": "<sup>20</sup> Orinani akacheuka, akaona mutumwa; vanakomana vake vana, vaiva naye, vakavanda. Zvino Orinani wakanga achipura zviyo.",
            "21": "<sup>21</sup> Dhavhidhi akati achisvika kuna Orinani, Orinani akatarira akaona Dhavhidhi, akabuda paburiro, akakotamira pasi nechiso chake pamberi paDhavhidhi.",
            "22": "<sup>22</sup> Ipapo Dhavhidhi akati kuna Orinani, \"Ndipe nzvimbo yeburiro iri, kuti ndivakirepo aritari kuna Jehovha; unofanira kundipa iro nditenge nomutengo wakafanira, kuti denda ripere pakati pavanhu.\"",
            "23": "<sup>23</sup> Ipapo Orinani akati kuna Dhavhidhi, \"Ritorei henyu, ishe wangu, mambo aite sezvaanoda; tarirai, ndinokupai nzombe zvive zvipiriso zvinopiswa, nenhumbi dzokupura nadzo dzive huni, nezviyo chive chipiriso choupfu; ndinozvipa zvose.\"",
            "24": "<sup>24</sup> Mambo Dhavhidhi akati kuna Orinani, \"Kwete, asi zvirokwazvo ndicharitenga nomutengo wakafanira; nokuti handingatori chinhu chako ndikapa Jehovha, kana kuuya nechipiriso chinopiswa chisina mutengo.\"",
            "25": "<sup>25</sup> Naizvozvo Dhavhidhi akapa Orinani mashekeri ana mazana matanhatu endarama pakurema kwawo, uri mutengo wenzvimbo iyo.",
            "26": "<sup>26</sup> Dhavhidhi akavakira Jehovha aritari ipapo, akabayira zvipiriso zvinopiswa nezvipiriso zvokuyananisa, akashumira Jehovha; iye akamupindura nomoto wakabva kudenga ukawira paaritari yechipiriso chinopiswa.",
            "27": "<sup>27</sup> Jehovha akaraira mutumwa, iye akadzoserazve munondo wake mumuhara wawo.",
            "28": "<sup>28</sup> Zvino nenguva iyo Dhavhidhi akati aona kuti Jehovha amupindura paburiro raOrinani muJebhusi, akabayirapo.",
            "29": "<sup>29</sup> Nokuti panguva iyo tabhenekari yaJehovha, yakanga yavakwa naMozisi murenje, nearitari yokupisira zvipiriso, zvakanga zviri padunhu rakakwirira paGibhiyoni.",
            "30": "<sup>30</sup> Asi Dhavhidhi akanga asingagoni kuendako kuzobvunza Mwari pamberi payo; nokuti akanga achitya nemhaka yomunondo womutumwa waJehovha."
        },
        "22":
        {
            "1": "<sup>1</sup> Dhavhidhi akati, \"Iyi ndiyo imba yaJehovha Mwari, neyiyi ndiyo aritari yokupisira zvipiriso zvaIsiraeri.\"",
            "2": "<sup>2</sup> Zvino Dhavhidhi akaraira kuti vatorwa vakanga vari panyika yaIsiraeri vaunganidzwe; akagadza vavezi vamabwe, kuti vaveze mabwe okuvaka nawo imba yaMwari.",
            "3": "<sup>3</sup> Dhavhidhi akagadzirawo matare mazhinji okupfura nawo mbambo dzamagonhi amasuo nezvikorekedzo; uye ndarira zhinji isina kuidzwa kurema kwayo.",
            "4": "<sup>4</sup> Namatanda emisidhari asina kuverengwa; nokuti vaZidhoni naveTire vakauya nemisidhari yakawanda kuna Dhavhidhi.",
            "5": "<sup>5</sup> Dhavhidhi akati, \"Soromoni mwanakomana wangu achiri muduku asine simba, uye imba, inofanira kuvakirwa Jehovha, inofanira kuva huru kwazvo, ingarumbidzwa nokukudzwa panyika dzose; saka ini ndichaigadzirira.\" Naizvozvo Dhavhidhi akagadzira zvizhinji asati afa.",
            "6": "<sup>6</sup> Ipapo akadana Soromoni mwanakomana wake, akamuraira kuti avakire Jehovha Mwari walsiraeri imba.",
            "7": "<sup>7</sup> Dhavhidhi akati kuna Soromoni mwanakomana wake, \"Kana ndirini, ndakanga ndichiti mumoyo mangu ndichavakira zita raJehovha Mwari wangu imba.",
            "8": "<sup>8</sup> Asi shoko raJehovha rakasvika kwandiri, richiti, `Iwe wakateura ropa rakawanda, uye wakarwa zvizhinji; iwe haungavakiri zita rangu imba, nokuti wakateura ropa rakawanda panyika pamberi pangu;",
            "9": "<sup>9</sup> asi tarira, uchaberekerwa mwanakomana, achava munhu wezororo; ndichamuzorodza pavavengi vake vose vanomupoteredza; nokuti zita rake richanzi Soromoni, ndichapa Isiraeri rugare nokunyarara namazuva ake;",
            "10": "<sup>10</sup> iye achavakira zita rangu imba; iye achava mwanakomana wangu, neni ndichava baba vake; ini ndichasimbisa chigaro chake choushe pakati paIsiraeri nokusingaperi.'",
            "11": "<sup>11</sup> \"Zvino, mwana wangu, Jehovha ngaave newe; ufambiswe zvakanaka iwe, uvake imba yaJehovha Mwari wako, sezvaakataura pamusoro pako.",
            "12": "<sup>12</sup> Asi Jehovha ngaakupe kungwara nokuchenjera, akuraire pamusoro paIsiraeri; kuti saizvozvo uchengete murayiro waJehovha Mwari wako.",
            "13": "<sup>13</sup> Nokudaro uchafambiswa zvakanaka, kana uchichenjera kuita mitemo nezvakatongwa, zvakarairwa Mozisi naJehovha pamusoro paIsiraeri; simba, utsunge moyo; usatya kana kuvhunduswa.",
            "14": "<sup>14</sup> Zvino tarira, ndakatambura ndichigadzirira imba yaJehovha matarenda ane zviuru zvine zana zvendarama, namatarenda ane chiuru chezviuru esirivha; nendarira namatare zvisina kuidzwa, nokuti zvakawanda kwazvo; ndakagadzirawo matanda namabwe; newe ungawedzera hako pazviri.",
            "15": "<sup>15</sup> Uye vabati unavo vazhinji, vaputsi navavezi vamabwe namatanda, navarume vose vakangwara pamabasa ose;",
            "16": "<sup>16</sup> nendarama, nesirivha, nendarira, namatare hazvingaverengwi; simuka, ubate Jehovha ave newe,\"",
            "17": "<sup>17</sup> Dhavhidhi akarairawo machinda ose aIsiraeri kuti vabatsire Soromoni mwanakomana wake, akati,",
            "18": "<sup>18</sup> \"Ko Jehovha Mwari wenyu haazi nemwi here? Haana kukuzorodzai kumativi ose here? Nokuti akaisa vanhu vagere panyika iyi muruoko rwangu; zvino nyika yakundwa pamberi paJehovha napamberi pavanhu vake.",
            "19": "<sup>19</sup> Zvino shingairai nemoyo yenyu uye nemweya yenyu kutsvaka Jehovha Mwari wenyu; naizvozvo simukai muvake imba tsvene yaJehovha Mwari, kuti mugoisa areka yesungano yaJehovha nenhumbi tsvene dzaMwari muimba inofanira kuvakirwa zita raJehovha.\""
        },
        "23":
        {
            "1": "<sup>1</sup> Zvino Dhavhidhi akanga akwegura, ava namazuva mazhinji; akaita Soromoni mwanakomana wake mambo walsiraeri.",
            "2": "<sup>2</sup> Akaunganidza machinda ose aIsiraeri pamwechete navaprista navaRevhi.",
            "3": "<sup>3</sup> VaRevhi vakaverengwa vanamakore makumi matatu navakapfuura ipapo; pakuwanda kwavo, vachiverengwa musoro womurume mumwe nomumwe, vakasvika zviuru zvina makumi matatu nezvisere.",
            "4": "<sup>4</sup> Pakati pavo vane zviuru zvina makumi maviri nezvina vaitarira mabasa eimba yaJehovha; vane zviuru zvitanhatu vaiva vatariri navatongi;",
            "5": "<sup>5</sup> vane zviuru zvina vaiva vatariri vemikova; vane zviuru zvina vairumbidza Jehovha nezvokuridza zvandakaita (ndizvo zvakareva Dhavhidhi) kuti arumbidzwe nazvo.",
            "6": "<sup>6</sup> Dhavhidhi akavakamura, akaita mapoka akaenzana navanakomana vaRevhi, Gerishoni, naKohati, naMerari.",
            "7": "<sup>7</sup> VokwaGerishoni: Radhani naShimei.",
            "8": "<sup>8</sup> Vanakomana vaRadhani: Jehieri mukuru, naZerami, naJoeri, vose vatatu.",
            "9": "<sup>9</sup> Vanakomana vaShimei: Sheromoti, naHazieri, naHarani, vose vatatu. Ndivo vaiva vakuru vedzimba dzamadzibaba aRadhani.",
            "10": "<sup>10</sup> Vanakomana vaShimei: Jahati, naZina, naJeushi, naBheriya. Ava vana vakanga vari vanakomana vaShimei.",
            "11": "<sup>11</sup> Jahati akanga ari mukuru, wechipiri Ziza; asi Jeushi naBheriya vakanga vasina vanakomana vazhinji saka vakaverengwa vachiita imba imwe yamadzibaba.",
            "12": "<sup>12</sup> Vanakomana vaKohati: Amirami, naIshari, naHebhuroni, naUzieri, vose vana.",
            "13": "<sup>13</sup> Vanakomana vaAmirami: Aroni, naMozisi; Aroni ndiye akatsaurwa, kuti anatse zvinhu zvitsene-tsvene, iye navanakomana vake, nokusingaperi; kuti vapise zvinonhuhwira pamberi paJehovha,nokumushumira, nokuropafadza vanhu nezita rake nokusingaperi.",
            "14": "<sup>14</sup> Asi kana ari Mozisi munhu waMwari, vanakomana vake vakaverengwa kurudzi rwaRevhi.",
            "15": "<sup>15</sup> Vanakomana vaMozisi: Gerishomi naEriezeri.",
            "16": "<sup>16</sup> Vanakomana vaGerishomi: Shebhueri mukuru.",
            "17": "<sup>17</sup> Vanakomana vaEriezeri: Rehabhia mukuru. Eriezeri akanga asina vamwe vanakomana; asi vanakomana vaRehabhia vakanga vari vazhinji-zhinji.",
            "18": "<sup>18</sup> Vanakomana valshari: Sheromiti mukuru.",
            "19": "<sup>19</sup> Vanakomana vaHebhuroni: Jeria mukuru, Amaria wechipiri, Jahazieri wechitatu, Jekameami wechina.",
            "20": "<sup>20</sup> Vanakomana vaUzieri: Mika mukuru, naIshia wechipiri.",
            "21": "<sup>21</sup> Vanakomana vaMerari: Mari naMushi. Vanakomana vaMari: Eriazari naKishi.",
            "22": "<sup>22</sup> Eriazari akafa asina vanakomana, asi vanasikana voga; hama dzavo, vanakomana vaKishi, dzikavawana.",
            "23": "<sup>23</sup> Vanakomana vaMushi: Mari, naEdheri, naJeremoti, vose vatatu.",
            "24": "<sup>24</sup> Ndivo vaiva vanakomana vaRevhi, nedzimba dzamadzibaba avo, ivo vakuru vedzimba dzamadzibaba vakaverengwa pakati pavo, vachiverengwa namazita avo musoro womumwe nomumwe, vaibata basa paimba yaJehovha, vanamakore makumi maviri navakapfuura ipapo.",
            "25": "<sup>25</sup> Nokuti Dhavhidhi wakati, \"Jehovha Mwari waIsiraeri wakazorodza vanhu vake; iye anogara Jerusaremu nokusingaperi,",
            "26": "<sup>26</sup> uye vaRevhi havachazofaniri kutakura tabhenekari nenhumbi dzayo dzokubata mabasa ayo.\"",
            "27": "<sup>27</sup> Nokuti namashoko aDhavhidhi okupedzisira vanakomana vaRevhi vakaverengwa, vanamakore makumi maviri navakapfuura ipapo.",
            "28": "<sup>28</sup> Nokuti basa ravo rakanga riri rokubatsira vanakomana vaAroni pakushumira paimba yaJehovha, pavazhe, napamakamuri, napakusuka zvinhu zvitsvene, riri basa rokushumira paimba yaMwari;",
            "29": "<sup>29</sup> nokugadzira zvingwa zvokuratidza, noupfu hwakatsetseka bwechipiriso choupfu, kana chezvingwa zvitete zvisina mbiriso, kana zvakabikwa mugango, kana zvakakanyiwa namafuta, marudzi ose ezviyero nezvitanda zvokuera",
            "30": "<sup>30</sup> nazvo, nokumira mangwanani ose vachivonga nokurumbidza Jehovha, uye madekwanawo saizvozvo;",
            "31": "<sup>31</sup> nokubayira Jehovha zvipiriso zvose zvinopiswa, pamaSabata, napakugara kwemwedzi, napamitambo yakatarwa panguva dzose dzakarairwa, mazuva ose pamberi paJehovha.",
            "32": "<sup>32</sup> Uye kuti vachengete tende rokusangana, nokuchengeta nzvimbo tsvene, nokuchengeta vanakomana vaAroni, hama dzavo, pabasa reimba yaJehovha."
        },
        "24":
        {
            "1": "<sup>1</sup> Zvino mapoka avanakomana vaAroni akanga akadai: vanakomana vaAroni: Nadhabhi, naAbhihu, naEriazari, naItamari.",
            "2": "<sup>2</sup> Asi Nadhabhi naAbhihu vakafa vasina vana, baba vavo vachiri vapenyu; saka Eriazari naItamari vakabata basa rouprista.",
            "3": "<sup>3</sup> Dhavhidhi, pamwechete naZadhoki, mumwe wavanakomana vaEriazari, naAhimereki, mumwe wavanakomana vaItamari, vakakamura sezvavakanga vagoverwa mabasa avo.",
            "4": "<sup>4</sup> Vakawana vakuru vazhinji pakati pavanakomana vaEriazari kupfuura pakati pavanakomana vaItamari; vakakamurwa kudai : Pakati pavanakomana vaEriazari kwakanga kuna vane gumi navatanhatu, vaiva vakuru vedzimba dzamadzibaba; napakati pavanakomana vaItamari, nedzimba dzamadzibaba avo vakanga vari vasere.",
            "5": "<sup>5</sup> Nokudaro vakakamurwa nemijenya, mumwe pano mumwe koko; nokuti kwakanga kuna machinda eimba tsvene, namachinda aMwari pakati pavanakomana valtamari.",
            "6": "<sup>6</sup> Shemaya mwanakomana waNetaneri, munyori, akanga ari wavaRevhi, akavanyora pamberi pamambo, namachinda, nomuprista Zadhoki, naAhimereki mwanakomana waAbhiyatari, navakuru vedzimba dzamadzibaba avaprista navaRevhi; vachitsaura mhuri imwe kuna Eriazari, nemhuri imwe kuna Itamari.",
            "7": "<sup>7</sup> Zvino mujenya wokutanga wakabata Jehorayaribhi, wechipiri Jedheiya;",
            "8": "<sup>8</sup> wechitatu Harimi, wechina Seorimi,",
            "9": "<sup>9</sup> weshanu Marikiya, wetanhatu, Miyamini,",
            "10": "<sup>10</sup> wechinomwe Hakozi, worusere Abhija;",
            "11": "<sup>11</sup> wepfumbamwe Jeshua, wegumi Shekania;",
            "12": "<sup>12</sup> wegumi nomumwe Eriashibhi, wegumi nemiviri Jakimi;",
            "13": "<sup>13</sup> wegumi nemitatu Hupa, wegumi nemina Jeshebhiyabhi;",
            "14": "<sup>14</sup> wegumi nemishanu Bhiriga, wegumi nemitanhatu Imeri;",
            "15": "<sup>15</sup> wegumi neminomwe Heziri, wegumi nemisere Hapizeri;",
            "16": "<sup>16</sup> wegumi nemipfumbamwe Petahia, wamakumi maviri Jehezekeri;",
            "17": "<sup>17</sup> wamakumi maviri nomumwe Jakini, wamakumi maviri nemiviri Gamuri;",
            "18": "<sup>18</sup> wamakumi maviri nemitatu Dheraya, wamakumi maviri nemina Maazia.",
            "19": "<sup>19</sup> Ndiko kukamurwa kwavo pamabasa avo, kuti vapinde imba yaJehovha pavakanga varairwa napo naAroni baba vavo, sezvaakarairwa naJehovha Mwari walsiraeri.",
            "20": "<sup>20</sup> Kana vari vamwe vanakomana vaRevhi, vanakomana vaAmirami: Shubhaeri; vavanakomana vaShubhaeri: Jedheiya.",
            "21": "<sup>21</sup> VaRehabhia: vavanakomana vaRehabhia: Ishia mukuru.",
            "22": "<sup>22</sup> Vavalzhari: Sheromoti; vavanakomana vaSheromoti: Jahati.",
            "23": "<sup>23</sup> Vavanakomana vaHebhuroni: Jeria mukuru, naAmaria wechipiri, naJahazieri wechitatu, naJekemeami wechina.",
            "24": "<sup>24</sup> Vanakomana vaUzieri: Mika; vavanakomana vaMika: Shamiri",
            "25": "<sup>25</sup> Munun'una waMika: Ishia; vavanakomana vaIshia Zekariya.",
            "26": "<sup>26</sup> Vanakomana vaMerari: Mari naMushi; vanakomana vaJazia: Bheno.",
            "27": "<sup>27</sup> Vanakomana vaMerari: vaJazia: Bheno, naShohami, naZakuri, naIbhiri.",
            "28": "<sup>28</sup> VaMari: Eriazari, wakanga asina vanakomana.",
            "29": "<sup>29</sup> VaKishi: vanakomana vaKishi: Jerameeri.",
            "30": "<sup>30</sup> Vanakomana vaMushi: Mari, naEdheri, naJerimoti. Ndivo vakanga vari vanakomana vaRevhi, nedzimba dzamadzibaba avo.",
            "31": "<sup>31</sup> Naivo vakakandawo mijenya sehama dzavo, ivo vanakomana vaAroni pamberi pamambo Dhavhidhi, naZadhoki, naAhimereki, navakuru vedzimba dzamadzibaba dzavaprista navaRevhi; dzimba dzamadzibaba dzomukuru dzaiverengwa sedzomunun'una wake."
        },
        "25":
        {
            "1": "<sup>1</sup> Dhavhidhi navakuru vehondo vakatsaurirawo basa ravo vanakomana vaAsafi naHemani naJedhutuni, kuti vaporofite nembira, nemitengeramwa, namakandira; mazita avakabata basa ravo ndiwo:",
            "2": "<sup>2</sup> vavanakomana vaAsafi: Zakuri, naJosefa, naNetania, naAsharera, vanakomana vaAsafi; vairairwa naAsafi, waiporofita nomurayiro wamambo.",
            "3": "<sup>3</sup> VaJedhutuni: vanakomana vaJedhutuni. Gedharia, naZeri, naJeshaya, naHashabhia, naMatitia, vatanhatu; ivo vairairwa nababa vavo Jedhutuni, nembira, waiporofita achivonga nokurumbidza Jehovha.",
            "4": "<sup>4</sup> VaHemani: vanakomana vaHemani: Bhukia, naMatania, naUzieri, naShebhueri, naJerimoti, naHanania, naHanani, naEriata, naGidhareti, naRomamitiezeri, naJoshibhekasha, naMaroti, naHotiri, naMahazioti.",
            "5": "<sup>5</sup> Ava vose vakanga vari vanakomana vaHemani muoni wamambo pamashoko aMwari; Mwari akapa Hemani vanakomana vane gumi navana, navakunda vatatu, kuzomusimbisa.",
            "6": "<sup>6</sup> Ava vose vairairwa nababa vavo pakuimba kwavo paimba yaJehovha namakandira, nemitengeramwa, nembira, pabasa reimba yaMwari; Asafi, naJedhutuni, naHemani vairairwa namambo.",
            "7": "<sup>7</sup> Ava vose, pamwechete nehama dzavo dzakanga dzadzidziswa kuimbira Jehovha, ivo vose vakanga vachigona kwazvo, vakasvika mazana maviri namakumi masere navasere.",
            "8": "<sup>8</sup> Vakakanda mijenya kuzoratidzwa mabasa avo, kusina anodarikwa, vaduku pamwechete navakuru, navadzidzisi pamwechete navadzidzi.",
            "9": "<sup>9</sup> Mujenya wokutanga waAsafi ukabata Josefa; wechipiri Gedharia; iye navanun'una vake vakanga vari gumi navaviri;",
            "10": "<sup>10</sup> wechitatu Zakuri, navanakomana vake navanun'una vake, gumi navaviri;",
            "11": "<sup>11</sup> wechina Iziri, navanakomana vake navanun'una vake, gumi navaviri;",
            "12": "<sup>12</sup> weshanu Netania, navanakomana vake navanun'una vake, gumi navaviri;",
            "13": "<sup>13</sup> wetanhatu Bhukia, navanakomana vake, navanun'una vake, gumi navaviri;",
            "14": "<sup>14</sup> wechinomwe Jesharera, navanakomana vake navanun'una vake, gumi navaviri;",
            "15": "<sup>15</sup> worusere Jeshaya, navanakomana vake navanun'una vake, gumi navaviri;",
            "16": "<sup>16</sup> wepfumbamwe Matania, navanakomana vake navanun'una vake, gumi navaviri;",
            "17": "<sup>17</sup> wegumi Shimei, navanakomana vake navanun'una vake, gumi navaviri;",
            "18": "<sup>18</sup> wegumi nomumwe Azareri, navanakomana vake navanun'una vake, gumi navaviri;",
            "19": "<sup>19</sup> wegumi navaviri Hashabhia, navanakomana vake navanun'una vake, gumi navaviri;",
            "20": "<sup>20</sup> wegumi navatatu Shubhaeri, navanakomana vake navanun'una vake, gumi navaviri;",
            "21": "<sup>21</sup> wegumi navana Matitia, navanakomana vake, navanun'una vake, gumi navaviri;",
            "22": "<sup>22</sup> wegumi navashanu Jeremoti, navanakomana vake navanun'una vake, gumi navaviri;",
            "23": "<sup>23</sup> wegumi navatanhatu Hanania, navanakomana vake navanun'una vake, gumi navaviri;",
            "24": "<sup>24</sup> wegumi navanomwe Joshibhekasha, navanakomana vake navanun'una vake, gumi navaviri;",
            "25": "<sup>25</sup> wegumi navasere Hanani, navanakomana vake navanun'una vake, gumi navaviri;",
            "26": "<sup>26</sup> wegumi navapfumbamwe Maroti, navanakomana vake navanun'una vake, gumi navaviri;",
            "27": "<sup>27</sup> wamakumi maviri Eriati, navanakomana vake navanun'una vake, gumi navaviri;",
            "28": "<sup>28</sup> wamakumi maviri nomumwe Hotiri, navanakomana vake navanun'una vake, gumi navaviri;",
            "29": "<sup>29</sup> wamakumi maviri navaviri Gidhareti, navanakomana vake navanun'una vake, gumi navaviri;",
            "30": "<sup>30</sup> wamakumi maviri navatatu Mahazioti, navanakomana vake navanun'una vake, gumi navaviri;",
            "31": "<sup>31</sup> wamakumi maviri navana Romamitiezeri, navanakomana vake navanun'una vake, gumi navaviri."
        },
        "26":
        {
            "1": "<sup>1</sup> Mapoka avatariri vemikova akanga akadai: vavaKora: Mesheremia mwanakomana waKore, wavanakomana vaAsafi.",
            "2": "<sup>2</sup> Mesheremia akanga ana vanakomana ava: Zekariya wedangwe, wechipiri Jedhieri; wechitatu Zebhedhia, wechina Jatinieri;",
            "3": "<sup>3</sup> wechishanu Erami, wechitanhatu Johwanani, wechinomwe Erihonai.",
            "4": "<sup>4</sup> NaObhedhi-Edhomu akanga ana vanakomana ava: Shemaya wedangwe, wechipiri Jehozabhadhi, wechitatu Joa, wechina Sakari, weshanu Netaneri;",
            "5": "<sup>5</sup> wetanhatu Amieri, wechinomwe Isakari, worusere Peuretai; nokuti Mwari akanga amuropafadza.",
            "6": "<sup>6</sup> NaShemaya mwanakomana wake, akaberekerwawo vanakomana vakabata, imba yababa vavo; nokuti vakanga vari varume vane simba noumhare.",
            "7": "<sup>7</sup> Vanakomana vaShemaya vakanga vari: Otini, naRefaeri, naObhedhi, naErizhabhadhi; vanun'una vake Erihu naShemakia, vakanga vari varume voumhare.",
            "8": "<sup>8</sup> Ava vose vakanga vari vanakomana va Obhedhi-Edhomu; ivo navanakomana vavo, navanun'una vavo vakanga vari varume vane simba vaigona kubata basa; vakanga vana makumi matanhatu navaviri vaObhedhi-Edhomu.",
            "9": "<sup>9</sup> Mesheremia akanga ana vanakomana navanun'una, varume voumhare, vane gumi navasere.",
            "10": "<sup>10</sup> NaHosavo, mumwe wavanakomana vaMerari, akanga ana vanakomana; mukuru akanga ari Shimiri, (nokuti kunyange akanga asati ari wedangwe baba vake vakamuita mukuru;)",
            "11": "<sup>11</sup> wechipiri Hirikia, wechitatu Tebharia, wechina Zekariya; vanakomana vose vaHosa navanun'una vake vakasvika gumi navatatu.",
            "12": "<sup>12</sup> Mapoka avatariri vemikova vakabva pakati paivawo, pakati pavarume vakuru, vakapiwa basa savanun'una vavo kubata muimba yaJehovha.",
            "13": "<sup>13</sup> Vakakanda mijenya, vaduku pamwechete navakuru, nedzimba dzamadzibaba avo kuzoratidzwa suo rimwe nerimwe.",
            "14": "<sup>14</sup> Sheremia akaratidzwa mugove wokumabvazuva. Zvino vakakanda mijenya pamusoro paZekariya mwanakomana wake, gurukota rakanga rakangwara; mugove wake ukaratidzwa kurutivi rwokumusoro.",
            "15": "<sup>15</sup> WaObhedhi-Edhomu kurutivi rwezasi; wavanakomana vake wokuchengeta dura.",
            "16": "<sup>16</sup> WaShupimi naHosa kumavirazuva, pasuo reShareketi pamugwagwa unokwira, varindi vakatarisana.",
            "17": "<sup>17</sup> Kumabvazuva kwakanga kuna vaRevhi vatanhatu, kurutivi rwokumusoro zuva rimwe nerimwe vana, kurutivi rwezasi zuva rimwe nerimwe vana, uye padura vaviri-vaviri.",
            "18": "<sup>18</sup> WaParibhari kumavirazuva, vana pamugwagwa, navaviri paParibhari.",
            "19": "<sup>19</sup> Ndiyo migove yavatariri vemikova, vavanakomana vavaKora navanakomana vaMerari.",
            "20": "<sup>20</sup> Pakati pavaRevhi Ahija aitarira zvivigiro zvefuma yeimba yaMwari, nezvivigiro zvefuma yezvinhu zvitsvene.",
            "21": "<sup>21</sup> Vanakomana vaRadhani: vanakomana vavaGerishoni vaiva vaRadhani, vakuru vedzimba dzamadzibaba aRadhani muGerishoni. Jehieri.",
            "22": "<sup>22</sup> Vanakomana vaJehieri: Zetami, naJoeri munun'una wake, vaitarira zvivigiro zveimba yaJehovha.",
            "23": "<sup>23</sup> Pakati pavaAmirami, navaIshari, navaHebhuroni, navaUzieri:",
            "24": "<sup>24</sup> Shebhueri mwanakomana waGerishomi, mwanakomana waMozisi, araira zvivigiro.",
            "25": "<sup>25</sup> Navanun'una vake: vaEriezeri, mwanakomana wake Rehabhia, nomwanakomana wake Jeshaya, nomwanakomana wake Joramu, nomwanakomana wake Zikiri, nomwanakomana wake Sheromoti.",
            "26": "<sup>26</sup> Sheromoti uyu navanun'una vake ndivo vaitarira zvivigiro zvose zvezvinhu zvitsvene, zvakanga zvatsaurwa namambo Dhavhidhi, navakuru vedzimba dzamadzibaba avo, navakuru vezviuru navamazana, navakuru vehondo.",
            "27": "<sup>27</sup> Pakati pezvakapambwa pakurwa vakatsaura zvimwe zvokugadzirisa nazvo imba yaJehovha.",
            "28": "<sup>28</sup> Zvose zvakanga zvatsaurwa naSamueri muoni, uye naSauro mwanakomana waKishi, naAbhineri mwanakomana waNeri, naJoabhu mwanakomana waZeruya; zvose zvakanga zvatsaurwa naani naani zvakanga zvichichengetwa naSheromoti navanun'una vake.",
            "29": "<sup>29</sup> Pakati pavaIshari, Kenania navanakomana vake vaibata mabasa okunze pakati paIsiraeri, vari vatariri navatongi.",
            "30": "<sup>30</sup> Pakati pavaHebhuroni, Hashabhia navanun'una vake, varume voumhare, vane chiuru chinamazana manomwe, vaitarira Isiraeri mhiri kwaJorodhani kumavirazuva, kuti vabate mabasa ose aJehovha namabasa amambo.",
            "31": "<sup>31</sup> Pakati pavaHebhuroni, Jeriya akanga ari mukuru, ipo pakati pavaHebhuroni, namarudzi avo nedzimba dzamadzibaba avo. Pagore ramakumi mana rokubata ushe kwaDhavhidhi vakatsvakwa, vakawana pakati pavo varume vane simba noumhare paJazeri reGiriyadhi.",
            "32": "<sup>32</sup> Vanun'una vake, varume voumhare, vakasvika zviuru zviviri namazana manomwe, vari vakuru vedzimba dzamadzibaba, vakanga vaitwa namambo Dhavhidhi vatariri vavaRubheni, navaGadhi, navehafu yorudzi rwaManase, kuti vaite zvose zvaMwari, namabasa ose amambo."
        },
        "27":
        {
            "1": "<sup>1</sup> Zvino vana vaIsiraeri vakaverengwa vakuru vedzimba dzamadzibaba, navakuru vezviuru navamazana, navatariri vavo vaibatira mambo pazvinhu zvose zvamapoka aipinda nokubuda mwedzi mumwe nomumwe, mwedzi yose yegore, boka rimwe nerimwe rikasvika zviuru zvina makumi maviri nezvina.",
            "2": "<sup>2</sup> Mutariri weboka rokutanga, pamwedzi wokutanga, akanga ari Jashobhiyami mwanakomana waZabhadhieri; paboka rake pakanga pane zviuru zvina makumi maviri nezvina.",
            "3": "<sup>3</sup> Iye akanga ari mumwe wavana vaPerezi, mukuru wavatariri vose vehondo pamwedzi wokutanga.",
            "4": "<sup>4</sup> Mutariri weboka romwedzi wechipiri akanga ari Dodai muAhohi, neboka rake; Mikiroti ndiye aivaraira; paboka rake pakanga pane zviuru zvina makumi maviri nezvina.",
            "5": "<sup>5</sup> Mukuru wechitatu wehondo pamwedzi wechitatu, akanga ari Bhenaya, mwanakomana womuprista Jehoyadha, iye mukuru; paboka rake pakanga pane zviuru zvina makumi maviri nezvina.",
            "6": "<sup>6</sup> Uyu ndiye Bhenaya uya, akanga ari munhu wesimba pakati pavana makumi matatu, waiva mutariri wavana makumi matatu; Amizabhadhi mwanakomana wake akanga ari weboka rake.",
            "7": "<sup>7</sup> Mukuru wechina, pamwedzi wechina, akanga ari Asaheri munun'una waJoabhu, naZebhedhia mwanakomana wake aimutevera; paboka rake pakanga pane zviuru zvina makumi maviri nezvina.",
            "8": "<sup>8</sup> Mukuru weshanu, pamwedzi weshanu, wakanga ari Shamhuti mulzirahi; paboka rake pakanga pane zviuru zvina makumi maviri nezvina.",
            "9": "<sup>9</sup> Mukuru wetanhatu, pamwedzi wetanhatu, akanga ari Ira mwanakomana waIkeshi muTekoite; paboka rake pakanga pane zviuru zvina makumi maviri nezvina.",
            "10": "<sup>10</sup> Mukuru wechinomwe, pamwedzi wechinomwe, akanga ari Herezi muPeroni, wavana vaEfuremu; paboka rake pakanga pane zviuru zvina makumi maviri nezvina.",
            "11": "<sup>11</sup> Mukuru wourusere, pamwedzi worusere, akanga ari Shebhekai muHushati, wavaZerahi; paboka rake pakanga pane zviuru zvina makumi maviri nezvina.",
            "12": "<sup>12</sup> Mukuru wepfumbamwe, pamwedzi wepfumbamwe, akanga ari Abhiezeri muAnatoti, wavaBhenjamini; paboka rake pakanga pane zviuru zvina makumi maviri nezvina.",
            "13": "<sup>13</sup> Mukuru wegumi, pamwedzi wegumi, akanga ari Maharai muNetofati, wavaZerahi; paboka rake pakanga pane zviuru zvina makumi maviri nezvina.",
            "14": "<sup>14</sup> Mukuru wegumi nomumwe, pamwedzi wegumi nomumwe, akanga ari Bhenaya muPiratoni, wavana vaEfuremu; paboka rake pakanga pane zviuru zvina makumi maviri nezvina.",
            "15": "<sup>15</sup> Mukuru wegumi navaviri, pamwedzi wegumi nemiviri, akanga ari Heridhai muNetofati, waOtinieri; paboka rake pakanga pane zviuru zvina makumi maviri nezvina.",
            "16": "<sup>16</sup> Uye, kwakanga kuna varairi vamarudzi alsiraeri; vokuvaRubheni akanga ari Eriezeri mwanakomana waZikiri, murairi; wokuvaSimiyoni; Shefatia mwanakomana waMaaka;",
            "17": "<sup>17</sup> wokwaRevhi: Hashabhia, mwanakomana waKemueri; wokwaAroni: Zadhoki;",
            "18": "<sup>18</sup> wokwaJudha: Erihu, mumwe wavanun'una vaDhavhidhi; wokwalsakari: Omiri mwanakomana waMikaeri;",
            "19": "<sup>19</sup> wokwaZebhuruni: Ishimaya mwanakomana waObhadhiya, wokwaNafutari: Jeremoti mwanakomana waAzieri;",
            "20": "<sup>20</sup> wokuvana vaEfuraimu: Hoshea mwanakomana waAzazia; wokuhafu yorudzi rwaManase: Joeri mwanakomana waPedhaya;",
            "21": "<sup>21</sup> wokuhafu yorudzi rwaManase paGiriyadhi, Idho mwanakomana waZekariya; wokwaBhenjamini: Jasieri mwanakomana waAbhineri;",
            "22": "<sup>22</sup> wokwaDhani: Azareri mwanakomana waJehorami. Ndivo vakanga vari vakuru vamarudzi alsiraeri.",
            "23": "<sup>23</sup> Asi Dhavhidhi haana kuverenga vamakore makumi maviri navaduku kwavari; nokuti Jehovha akataura kuti achawanza vaIsiraeri, vave senyeredzi dzokudenga.",
            "24": "<sup>24</sup> Joabhu mwanakomana waZeruya akatanga kuverenga, asi haana kupedza; kutsamwa kwaJehovha kukamukira valsiraeri nemhosva iyi; uye havana kuisa kuwanda kwavo pamakoronike amambo Dhavhidhi.",
            "25": "<sup>25</sup> Mutariri wefuma yamambo akanga ari Azimavheti mwanakomana waAdhieri mutariri wefuma yapaminda, napamaguta, napamisha, neyapanhare, akanga ari Jonatani mwanakomana waUziya;",
            "26": "<sup>26</sup> mutariri wavanhu vaibata paminda vachirima pasi, akanga ari Eziri mwanakomana waKerubhi.",
            "27": "<sup>27</sup> Mutariri weminda yemizambiringa, akanga ari Shimei muRamati; mutariri wezvibereko zveminda yemizambiringa, wedzimba dzewaini, akanga ari Zabhadhi muShifimi;",
            "28": "<sup>28</sup> mutariri wemiorivhi nemionde yakanga iri pamapani, akanga ari Bhaarihanani muGedheri; mutariri wedzimba dzamafuta akanga ari Joashi.",
            "29": "<sup>29</sup> Mutariri wamapoka ezvipfuwo, zvaifura paSharoni, akanga ari Shitirai muSharoni, uye mutariri wamapoka aigara pamipata, akanga ari Shafati mwanakomana waAdhirai;",
            "30": "<sup>30</sup> mutariri wamakamera akanga ari Obhiri muIshimaeri, uye mutariri wembongoro akanga ari Jedheiya muMeronoti;",
            "31": "<sup>31</sup> mutariri wamapoka amakwai akanga ari Jazizi muHagiri. Ava vose vakanga vari vatariri venhumbi dzamambo Dhavhidhi.",
            "32": "<sup>32</sup> Uye Jonatani muzukuru waDhavhidhi akanga ari gurukota, murume akangwara, munyori; Jehieri mwanakomana waHakimoni aigara navanakomana vamambo;",
            "33": "<sup>33</sup> Ahitoferi akanga ari gurukota ramambo; naHushai muAriki yaiva shamwari yamambo;",
            "34": "<sup>34</sup> aitevera Ahitoferi ndiJehoyadha mwanakomana waBhenaya, naAbhiyatari; mukuru wehondo yamambo akanga ari Joabhu."
        },
        "28":
        {
            "1": "<sup>1</sup> Zvino Dhavhidhi akaunganidza paJerusaremu machinda ose aIsiraeri, machinda amarudzi, navakuru vamapoka aibatira mambo vachiita mapoka, navakuru vezviuru, navakuru vamazana, navatariri venhumbi dzose nefuma yose yamambo navanakomana vake, pamwechete navaranda navane simba, varume vose voumhare.",
            "2": "<sup>2</sup> Ipapo mambo Dhavhidhi akasimuka, akamira namakumbo ake, akati, \"Chindinzwai hama dzangu, navanhu vangu! Kana ndirini, ndakanga ndichiti mumoyo mangu ndichavakira areka yesungano yaJehovha nechitsiko chaMwari wedu imba yokuzorora; zvino ndakanga ndagadzira zvose zvokuvaka nazvo.",
            "3": "<sup>3</sup> Asi Mwari akati kwandiri, `Iwe haufaniri kuvakira zita rangu imba, nokuti uri munhu wokurwa, wakateura ropa.'",
            "4": "<sup>4</sup> Asi Jehovha, Mwari waIsiraeri, akanditsaura paimba yose yababa vangu kuti ndive mambo waIsiraeri nokusingaperi; nokuti akatsaura Judha kuti ave mutungamiriri; paimba yaJudha akatsaura imba yababa vangu akandifarira ini, akandiita mambo wavaIsiraeri vose.",
            "5": "<sup>5</sup> Zvino pakati pavanakomana vangu vose nokuti Jehovha akandipa vanakomana vazhinji akatsaura mwanakomana wangu Soromoni, kuti agare pachigaro choushe chaJehovha muna Isiraeri.",
            "6": "<sup>6</sup> Akati kwandiri,\"Soromoni mwanakomana wako ndiye uchavaka imba yangu nevazhe dzangu; nokuti ndakamutsaura kuti ave mwanakomana wangu, neni ndichava baba vake.",
            "7": "<sup>7</sup> Ndichasimbisa ushe hwake nokusingaperi, kana iye achitsungirira kuita zvandakaraira nezvandakatonga, sezvaanoita nhasi.'",
            "8": "<sup>8</sup> \"Naizvozvo zvino, pamberi pavaIsiraeri vose, neungano yose yaJehovha, Mwari wedu achizvinzwa, chengetai nokutsvaka mirairo yose yaJehovha Mwari wenyu; kuti nyika ino yakanaka ive yenyu, mugoisiyira vana venyu vanokuteverai, ive nhaka yavo nokusingaperi.",
            "9": "<sup>9</sup> \"Zvino iwe Soromoni, mwanakomana wangu, ziva Mwari wababa vako, umushumire nomoyo wakarurama, uye nomweya unoda kwazvo; nokuti Jehovha anonzvera moyo yose, anonzwisisa ndangariro dzose dzemifungo kana ukamutsvaka, iye achawanikwa newe; asi kana ukamusiya, iye achakurasha nokusingaperi.",
            "10": "<sup>10</sup> Zvino chenjera, nokuti Jehovha akakutsaura, kuti uvake imba ive nzvimbo tsvene; chiva nesimba, uzviite.\"",
            "11": "<sup>11</sup> Ipapo Dhavhidhi akapa mwanakomana wake Soromoni mufananidzo webiravira retemberi, nedzimba dzaro, nezvivigiro zvefuma yaro, namakamuri aro okumusoro, namakamuri aro omukati, neimba yechifunhiro chokuyananisa;",
            "12": "<sup>12</sup> nemifananidzo yezvose yakanga iri mumweya wake, yevazhe dzeimba yaJehovha, neyamakamuri ose anokomberedza, neyezvivigiro zvefuma yeimba yaMwari, neyezvivigiro zvefuma yenhumbi dzakatsaurwa;",
            "13": "<sup>13</sup> neyamapoka avaprista navaRevhi, neyamabasa ose eimba yaJehovha, neyenhumbi dzose dzokubata nadzo paimba yaJehovha;",
            "14": "<sup>14</sup> neyendarama, kurema kwayo, yokuumba nayo nhumbi dzendarama, nhumbi dzose dzamarudzi ose amabasa; neyesirivha, kurema kwayo, yokuumba nayo nhumbi dzose dzesirivha, nhumbi dzose dzamarudzi ose amabasa;",
            "15": "<sup>15</sup> nokuremawo kwendarama yokuumba nayo zvigadziko zvendarama, nemwenje yazvo, kurema kwechigadziko chimwe nechimwe nemimwe yacho; nesirivha, kurema kwayo, yokuumba nayo zvigadziko zvendarama ,nemwenje yazvo kurema kwechigadziko chimwe nechimwe nemwenje yacho, sezvakakwanira basa rechigadziko chimwe nechimwe;",
            "16": "<sup>16</sup> nendarama, kurema kwayo, yokuumba nayo tafura dzezvingwa zvokuratidza, yetafura imwe neimwe; nesirivha yetafura dzesirivha;",
            "17": "<sup>17</sup> nezvikokovonho zvendarama yakaisvonaka; nemidziyo yendarama, kurema komudziyo mumwe nomumwe; nemidziyo yesirivha, kurema komudziyo mumwe nomumwe;",
            "18": "<sup>18</sup> nokurema kwendarama yakanatswa yearitari yezvipfungaidzo zvinonhuhwira; nendarama yomufananidzo wengoro, wamakerubhi akatambanudza mapapiro awo, achifukidza areka yesungano yaJehovha.",
            "19": "<sup>19</sup> Izvi zvose, ndizvo zvakataura Dhavhidhi, ndakazviziviswa zvakanyorwa noruoko rwaJehovha, iwo mabasa ose omufananidzo uyu.",
            "20": "<sup>20</sup> Zvino Dhavhidhi akati kuna Soromoni mwanakomana wake, \"Simba, utsunge moyo, uzviite; usatya kana kuvunduswa, nokuti Jehovha Mwari, iye Mwari wangu, anewe; haangakuregi, kana kukusiya, kusvikira mabasa ose okushumira nawo paimba yaJehovha apedzwa.",
            "21": "<sup>21</sup> Zvino tarira, ndiwo mapoka avaprista navaRevhi, vangabata mabasa ose eimba yaMwari; uyezve pamabasa ose uchava nemhizha dzose dzinoda kubata nomoyo wadzo mabasa amarudzi ose; navakuru navanhu vose vachateerera chose mirairo yako.\""
        },
        "29":
        {
            "1": "<sup>1</sup> Zvino mambo Dhavhidhi akati kuungano yose, \"Mwanakomana wangu Soromoni, wakatsaurwa iye oga naMwari, achiri muduku asine simba, asi basa iguru; nokuti imba huru haizi yomunhu, asi ndeyaJehovha Mwari.",
            "2": "<sup>2</sup> \"Zvino ini nesimba rangu rose ndakagadzirira imba yaMwari wangu ndarama yokuumba nayo zvinhu zvendarama, nesirivha yezvinhu zvesirivha, nendarira yezvinhu zvendarira, namatare ezvinhu zvamatare, namatanda ezvinhu zvamatanda; nezvibwe zveonikisi, nezvibwe zvokuisa mukati, nezvibwe zvoukomba, nezvina mavara-mavara, namamwe marudzi mazhinji ezvibwe zvinokosha, namabwe machena akawanda.",
            "3": "<sup>3</sup> Uyezve, zvandakada zvikuru imba yaMwari wangu, ini zvandine fuma pachangu yendarama nesirivha, ndinozvipa imba yaMwari wangu, ndichiwedzera pane zvose zvandakagadzirira imba tsvene;",
            "4": "<sup>4</sup> zvinoti matarenda endarama ane zviuru zvitatu, endarama yeOfiri, namatarenda akanatswa esirivha ane zviuru zvinomwe, kufukidza madziro eimba nazvo;",
            "5": "<sup>5</sup> nendarama yokuumba nayo zvinhu zvendarama, nesirivha yezvinhu zvesirivha, nezvamabasa ose anofanira kuitwa namaoko avapfuri. Zvino ndianiko anoda kupa nhendo dzake nomoyo wake wose kuna Jehovha?\"",
            "6": "<sup>6</sup> Ipapo machinda edzimba dzamadzibaba, namachinda amarudzi alsiraeri, navakuru vezviuru navamazana, pamwechete navatariri vamabasa amambo vakapa nomoyo wavo wose;",
            "7": "<sup>7</sup> vakapa kubata basa reimba yaMwari ndarama, matarenda ane zviuru zvishanu, namadhariki ane zviuru zvine gumi, nesirivha, matarenda ane zviuru zvine gumi, nendarira, matarenda ane zviuru zvine gumi nezvisere, namatare, matarenda ane zviuru zvine zana.",
            "8": "<sup>8</sup> Vakanga vane zvibwe zvinokosha vakazvipa zviiswe pafuma yeimba yaJehovha, zvichitarirwa naJehieri muGerishoni.",
            "9": "<sup>9</sup> Ipapo vanhu vakafara zvavakapa nomoyo wose, nokuti vakapa Jehovha nomoyo wose wakarurama; mambo Dhavhidhiwo akafara nomufaro mukuru.",
            "10": "<sup>10</sup> Zvino Dhavhidhi akakudza Jehovha pamberi peungano yose; Dhavhidhi akati, \"Imwi mukudzwe, Jehovha, Mwari waIsiraeri baba vedu nokusingaperi.",
            "11": "<sup>11</sup> Ukuru ndobwenyu, Jehovha, nesimba, nokubwinya, nokukunda, noumambo, nokuti zvose zviri kudenga nezviri pasi ndezvenyu; ushe ndobwenyu, Jehovha, makakudzwa muri musoro wezvose.",
            "12": "<sup>12</sup> fuma nokukudzwa zvinobva kwamuri, imwi munobata ushe pamusoro pezvose; paruoko rwenyu pane simba noushe, ruoko rwenyu ndirwo runokurisa nokupa vose simba.",
            "13": "<sup>13</sup> \"Saka zvino Mwari wedu, tinokuvongai nokurumbidza zita renyu rinobwinya.",
            "14": "<sup>14</sup> \"Asi ini ndini ani, navanhu vangu chinyiko, zvatingagona kupa nomoyo wose kudai? Nokuti zvose zvinobva kwamuri, takangokupai zvakabva paruoko rwenyu.",
            "15": "<sup>15</sup> Nokuti tiri vaeni pamberi penyu navatorwa, sezvakanga zvakaita madzibaba edu; mazuva edu panyika akafanana nomumvuri, hakune tariro.",
            "16": "<sup>16</sup> Jehovha Mwari wedu, izvi zvose zvakawanda zvatakagadzira kukuvakirai imba yezita renyu dzvene, zvinobva paruoko rwenyu; zvose ndezvenyu;",
            "17": "<sup>17</sup> ndinozivawo Mwari wangu, kuti imwi munoidzawo moyo, munofanira kurarama. Kana ndirini, ndakapa izvi zvose nomoyo wakarurama nomoyo wose; zvino ndakafara ndichiona vanhu venyu vari pano vachikupai nomoyo wose.",
            "18": "<sup>18</sup> Jehovha Mwari waAbhurahamu, nalsaka, naIsiraeri, madzibaba edu, garisai izvi nokusingaperi pandangariro dzemifungo yemoyo yavanhu venyu; rereudzai moyo yavo kwamuri,",
            "19": "<sup>19</sup> ipai Soromoni mwanakomana wangu moyo wakarurama, kuti achengete mirairo yenyu, nezvipupuriro zvenyu, nezvamakatema, kuti aite zvinhu izvi zvose, avake imba huru yandakagadzirira zvose.\"",
            "20": "<sup>20</sup> Ipapo Dhavhidhi akati kuungano yose, \"Zvino kudzai Jehovha Mwari wenyu.\" Ipapo ungano yakakudza Jehovha Mwari wamadzibaba avo, vakakotamisa misoro yavo, vakanamata kuna Jehovha namambo.",
            "21": "<sup>21</sup> Vakabayira Jehovha zvibayiro, vakauya nezvipiriso zvinopiswa kuna Jehovha mangwanani pazuva rakatevera, zvaiti: nzombe dzine chiuru chimwe, namakondobwe ane chiuru chimwe, namakwaiana ane chiuru chimwe, pamwechete nezvipiriso zvazvo zvinodururwa, nezvibayiro zvakawanda zvavaIsiraeri vose;",
            "22": "<sup>22</sup> vakadya, vakamwa pamberi paJehovha nomufaro mukuru nezuva iro. Vakaita Soromoni mwanakomana waDhavhidhi mambo rwechipiri, vakamuzodzera Jehovha ave mutungamiriri, naZadhoki ave muprista.",
            "23": "<sup>23</sup> Ipapo Soromoni akagara pachigaro choushe chaJehovha akava mambo panzvimbo yaDhavhidhi baba vake, akafambiswa zvakanaka; vaIsiraeri vose vakamuteerera.",
            "24": "<sup>24</sup> Machinda ose, navanhu vose vane simba, navanakomana vose vamambo Dhavhidhi vakazviisa pasi pamambo Soromoni.",
            "25": "<sup>25</sup> Jehovha akakudza Soromoni kwazvo-kwazvo pamberi pavaIsiraeri vose, akamupa umambo hwakakura, husina kumbopiwa mumwe mambo waIsiraeri wakamutangira.",
            "26": "<sup>26</sup> Zvino Dhavhidhi mwanakomana waJese akabata valsiraeri vose paushe hwake.",
            "27": "<sup>27</sup> Nguva yaakabata ushe hwaIsiraeri aiva makore makumi mana; wakabata ushe paHebhuroni makore manomwe, napaJerusaremu akabata ushe makore makumi matatu namatatu.",
            "28": "<sup>28</sup> Akazofa akwegura kwazvo, ava namakore mazhinji, nefuma, nokukudzwa; Soromoni mwanakomana wake akabata ushe panzvimbo yake.",
            "29": "<sup>29</sup> Zvino mabasa amambo Dhavhidhi, okutanga nookupedzisira, tarirai, akanyorwa pamashoko omuoni Samueri, napamashoko omuprista Natani, napamashoko omuoni Gadhi;",
            "30": "<sup>30</sup> pamwechete namashoko oushe hwake, nesimba rake, nezvaakaitirwa panguva dzose, iye navaIsiraeri, noushe hwose bwedzimwe nyika."
        }
    }

};
module.exports = book;